import React, { useEffect, useState } from "react";
import PocketBase from "pocketbase";
import "./VolumeConverter.css";

const pb = new PocketBase("http://127.0.0.1:8090");
pb.autoCancellation(false);

const VolumeConverter = () => {
  const [units, setUnits] = useState([]);
  const [selectedUnit, setSelectedUnit] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [toUnit1, setToUnit1] = useState("");
  const [toUnit2, setToUnit2] = useState("");
  const [toUnit3, setToUnit3] = useState("");

  useEffect(() => {
    const fetchCategoryId = async () => {
      try {
        const res = await pb.collection("categories").getFullList();
        const volumeCategory = res.find((cat) => cat.name === "Volume");
        if (volumeCategory) setCategoryId(volumeCategory.id);
      } catch (err) {
        console.error("Failed to fetch category ID:", err);
      }
    };

    fetchCategoryId();
  }, []);

  useEffect(() => {
    if (categoryId) {
      pb.collection("units")
        .getFullList({ filter: `category = "${categoryId}"` })
        .then(setUnits)
        .catch(console.error);
    }
  }, [categoryId]);

  const getConvertedValue = (toUnitId) => {
    if (!inputValue || !selectedUnit || !toUnitId) return "";
    const fromUnit = units.find((u) => u.id === selectedUnit);
    const toUnit = units.find((u) => u.id === toUnitId);
    if (!fromUnit || !toUnit) return "";
    return (
      (parseFloat(inputValue) * fromUnit.to_base_factor) / toUnit.to_base_factor
    ).toFixed(6);
  };

  return (
    <div className="converter-container">
      <div className="input-panel">
        <h3>Volume Converter</h3>
        <label>Enter value:</label>
        <input
          type="number"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
        <label>Select from unit:</label>
        <select
          value={selectedUnit}
          onChange={(e) => setSelectedUnit(e.target.value)}
        >
          <option value="">-- Select unit --</option>
          {units.map((unit) => (
            <option key={unit.id} value={unit.id}>
              {unit.name} ({unit.symbol})
            </option>
          ))}
        </select>
      </div>

      <div className="results-panel">
        <h4>Convert To</h4>
        <div className="convert-boxes">
          {[toUnit1, toUnit2, toUnit3].map((toUnit, index) => {
            const setToUnit = [setToUnit1, setToUnit2, setToUnit3][index];
            return (
              <div key={index} className="convert-box">
                <div className="result">
                  {getConvertedValue(toUnit)}
                </div>
                <select
                  value={toUnit}
                  onChange={(e) => setToUnit(e.target.value)}
                >
                  <option value="">-- Select unit --</option>
                  {units.map((unit) => (
                    <option key={unit.id} value={unit.id}>
                      {unit.name} ({unit.symbol})
                    </option>
                  ))}
                </select>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default VolumeConverter;
