import React, { useState, useEffect } from "react";
import PocketBase from "pocketbase";
import "./VolumeConverter.css";

const pb = new PocketBase("http://127.0.0.1:8090");
pb.autoCancellation(false);

const formatExponent = (val) => {
  if (!val || isNaN(val)) return "...";
  const [base, expRaw] = val.toExponential(2).split("e");
  const exp = expRaw.replace("+", "");
  return (
    <>
      {parseFloat(base).toFixed(2)}×10<sup>{exp}</sup>
    </>
  );
};

const formatNumber = (value, scientific) => {
  if (value === null || value === undefined || isNaN(value)) return "...";
  if (scientific) return formatExponent(value);
  return parseFloat(value).toLocaleString(undefined, {
    minimumFractionDigits: 2,
    maximumFractionDigits: 6,
  });
};

const RealWorldBox = ({
  selected,
  setSelected,
  items,
  volumeM3,
  scientific,
}) => {
  const sortedItems = [...items].sort(
    (a, b) => a.scientific_value - b.scientific_value
  );
  const result =
    selected && volumeM3 && selected.scientific_value
      ? volumeM3 / selected.scientific_value
      : null;

  return (
    <div className="bg-white shadow border rounded-lg p-4 flex flex-col gap-3">
      <div className="text-xl font-bold text-blue-600 text-center break-words">
        {formatNumber(result, scientific)}
      </div>

      <select
        value={selected?.id || ""}
        onChange={(e) => {
          const item = items.find((obj) => obj.id === e.target.value);
          if (item) setSelected(item);
        }}
        className="border p-2 rounded w-full text-sm"
      >
        {sortedItems.map((item) => (
          <option key={item.id} value={item.id}>
            {item.name}
          </option>
        ))}
      </select>

      {selected && (
        <div className="text-sm text-gray-700 bg-gray-50 rounded p-2 border">
          <div className="font-semibold text-center">{selected.name}</div>
          <div className="flex justify-between text-xs mt-1">
            <span className="text-blue-600">
              {formatNumber(selected.scientific_value, true)}
            </span>
            <span>
              {isNaN(selected.approx_value)
                ? "..."
                : formatNumber(
                    parseFloat(selected.approx_value),
                    false
                  )}{" "}
              {selected.expand?.unit?.symbol || ""}
            </span>
          </div>
          {selected.notes && (
            <div className="text-center text-gray-500 italic mt-2">
              {selected.notes}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default function VolumeConverter() {
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [selectedFromUnit, setSelectedFromUnit] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [toUnit1, setToUnit1] = useState("");
  const [toUnit2, setToUnit2] = useState("");
  const [toUnit3, setToUnit3] = useState("");

  const [sciConv1, setSciConv1] = useState(false);
  const [sciConv2, setSciConv2] = useState(false);
  const [sciConv3, setSciConv3] = useState(false);
  const [sciReal1, setSciReal1] = useState(false);
  const [sciReal2, setSciReal2] = useState(false);
  const [sciReal3, setSciReal3] = useState(false);

  const [real1, setReal1] = useState(null);
  const [real2, setReal2] = useState(null);
  const [real3, setReal3] = useState(null);

  useEffect(() => {
    const fetchCategory = async () => {
      const categories = await pb.collection("categories").getFullList();
      const volume = categories.find((c) => c.name === "Volume");
      if (volume) setCategoryId(volume.id);
    };
    fetchCategory();
  }, []);

  useEffect(() => {
    if (categoryId) {
      pb.collection("units")
        .getFullList({ filter: `category = "${categoryId}"` })
        .then(setUnits)
        .catch(console.error);

      pb.collection("realworld_items")
        .getFullList({ filter: `category = "${categoryId}"`, expand: "unit" })
        .then((items) => {
          setRealWorldItems(items);
          setReal1(items[0]);
          setReal2(items[1]);
          setReal3(items[2]);
        })
        .catch(console.error);
    }
  }, [categoryId]);

  const getConverted = (toUnitId, scientific) => {
    if (!inputValue || !selectedFromUnit || !toUnitId) return "";
    const from = units.find((u) => u.id === selectedFromUnit);
    const to = units.find((u) => u.id === toUnitId);
    if (!from || !to) return "";
    const result =
      (parseFloat(inputValue) * from.to_base_factor) / to.to_base_factor;
    return formatNumber(result, scientific);
  };

  const inputToM3 = () => {
    const from = units.find((u) => u.id === selectedFromUnit);
    return from && inputValue
      ? parseFloat(inputValue) * from.to_base_factor
      : null;
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-6 bg-white rounded-lg shadow">
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 items-start">
        {/* Left Panel */}
        <div className="space-y-4 flex flex-col justify-center min-h-[300px]">
          <h3 className="text-lg font-semibold text-center">From</h3>
          <input
            type="number"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            className="border p-2 rounded w-full"
            placeholder="Enter value"
          />
          <select
            value={selectedFromUnit}
            onChange={(e) => setSelectedFromUnit(e.target.value)}
            className="border p-2 rounded w-full"
          >
            <option value="">-- Select unit --</option>
            {[...units]
              .sort((a, b) => a.to_base_factor - b.to_base_factor)
              .map((u) => (
                <option key={u.id} value={u.id}>
                  {u.name} ({u.symbol})
                </option>
              ))}
          </select>
        </div>

        {/* Right Panel */}
        <div className="md:col-span-3 space-y-10">
          {/* Conversion Section */}
          <div>
            <h2 className="text-2xl font-bold mb-4 text-center">Conversion</h2>

            {/* Conversion Toggles */}
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 mb-4">
              {[sciConv1, sciConv2, sciConv3].map((toggle, index) => {
                const setToggle =
                  index === 0 ? setSciConv1 : index === 1 ? setSciConv2 : setSciConv3;
                return (
                  <div key={index} className="flex justify-center">
                    <button
                      onClick={() => setToggle(false)}
                      className={`px-3 py-1 text-sm rounded-l ${!toggle ? "bg-blue-600 text-white" : "bg-white border"}`}
                    >
                      General
                    </button>
                    <button
                      onClick={() => setToggle(true)}
                      className={`px-3 py-1 text-sm rounded-r ${toggle ? "bg-blue-600 text-white" : "bg-white border"}`}
                    >
                      Scientific
                    </button>
                  </div>
                );
              })}
            </div>

            {/* Conversion Boxes */}
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
              {[toUnit1, toUnit2, toUnit3].map((id, index) => {
                const setter =
                  index === 0 ? setToUnit1 : index === 1 ? setToUnit2 : setToUnit3;
                const toggle = index === 0 ? sciConv1 : index === 1 ? sciConv2 : sciConv3;

                return (
                  <div key={index} className="border rounded p-4 bg-white shadow-sm">
                    <div className="text-xl font-bold text-blue-600 mb-2 text-center break-words">
                      {getConverted(id, toggle) || "..."}
                    </div>
                    <select
                      value={id}
                      onChange={(e) => setter(e.target.value)}
                      className="border p-2 rounded w-full text-sm text-center"
                    >
                      <option value="">-- Convert To --</option>
                      {[...units]
                        .sort((a, b) => a.to_base_factor - b.to_base_factor)
                        .map((u) => (
                          <option key={u.id} value={u.id}>
                            {u.name} ({u.symbol}) – {getConverted(u.id, toggle)}
                          </option>
                        ))}
                    </select>
                  </div>
                );
              })}
            </div>
          </div>

          {/* Real-World Comparison Section (Always Visible) */}
          <div>
            <h2 className="text-2xl font-bold mb-4 text-center">Comparison</h2>
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 mb-4">
              {[sciReal1, sciReal2, sciReal3].map((sci, index) => {
                const setToggle =
                  index === 0 ? setSciReal1 : index === 1 ? setSciReal2 : setSciReal3;
                return (
                  <div key={index} className="flex justify-center">
                    <button
                      onClick={() => setToggle(false)}
                      className={`px-3 py-1 text-sm rounded-l ${!sci ? "bg-blue-600 text-white" : "bg-white border"}`}
                    >
                      General
                    </button>
                    <button
                      onClick={() => setToggle(true)}
                      className={`px-3 py-1 text-sm rounded-r ${sci ? "bg-blue-600 text-white" : "bg-white border"}`}
                    >
                      Scientific
                    </button>
                  </div>
                );
              })}
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
              {[real1, real2, real3].map((realItem, index) => {
                const setSelected =
                  index === 0 ? setReal1 : index === 1 ? setReal2 : setReal3;
                const sci =
                  index === 0 ? sciReal1 : index === 1 ? sciReal2 : sciReal3;
                return (
                  <RealWorldBox
                    key={index}
                    selected={realItem}
                    setSelected={setSelected}
                    items={realWorldItems}
                    volumeM3={inputToM3()}
                    scientific={sci}
                  />
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
