import React, { useState, useEffect } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber } from "../utils/formatNumber";

function Converter({ categoryId }) {
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [fromUnit, setFromUnit] = useState("");
  const [conversionToggles, setConversionToggles] = useState([false, false, false]);
  const [comparisonToggles, setComparisonToggles] = useState([false, false, false]);
  const [selectedUnits, setSelectedUnits] = useState(["", "", ""]);
  const [selectedItems, setSelectedItems] = useState(["", "", ""]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const unitList = await pb.collection("units").getFullList({
          filter: `category = "${categoryId}"`,
        });
        const sorted = unitList.sort((a, b) => a.to_base_factor - b.to_base_factor);
        setUnits(sorted);
        setFromUnit(sorted[0]?.id || "");
        setSelectedUnits([sorted[1]?.id || "", sorted[2]?.id || "", sorted[3]?.id || ""]);

        const itemList = await pb.collection("realworld_items").getFullList({
          filter: `category = "${categoryId}"`,
          expand: "unit",
        });
        const sortedItems = itemList.sort((a, b) => a.scientific_value - b.scientific_value);
        setRealWorldItems(sortedItems);
        setSelectedItems([sortedItems[0], sortedItems[1], sortedItems[2]]);
      } catch (err) {
        console.error("Converter fetch error:", err);
      }
    };
    if (categoryId) fetchData();
  }, [categoryId]);

  const getConvertedValue = (toUnitId) => {
    const from = units.find((u) => u.id === fromUnit);
    const to = units.find((u) => u.id === toUnitId);
    if (!from || !to || !inputValue) return null;
    const base = parseFloat(inputValue) * from.to_base_factor;
    return base / to.to_base_factor;
  };

  const getComparisonValue = (index) => {
    const from = units.find((u) => u.id === fromUnit);
    const approx = selectedItems[index]?.approx_value;
    if (!from || !approx || !inputValue) return null;
    const baseValue = parseFloat(inputValue) * from.to_base_factor;
    return baseValue / approx;
  };

  const toggleConversion = (index) => {
    const updated = [...conversionToggles];
    updated[index] = !updated[index];
    setConversionToggles(updated);
  };

  const toggleComparison = (index) => {
    const updated = [...comparisonToggles];
    updated[index] = !updated[index];
    setComparisonToggles(updated);
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
      {selectedUnits.map((unitId, index) => (
        <div key={index}>
          {/* Conversion Box */}
          <div className="bg-white p-4 rounded shadow">
            <div className="text-center text-lg font-semibold mb-2">Conversion</div>
            <div className="flex justify-center gap-2 mb-2">
              <button
                className={`px-4 py-1 rounded-l ${!conversionToggles[index] ? "bg-blue-600 text-white" : "bg-white border"}`}
                onClick={() => toggleConversion(index)}
              >
                General
              </button>
              <button
                className={`px-4 py-1 rounded-r ${conversionToggles[index] ? "bg-blue-600 text-white" : "bg-white border"}`}
                onClick={() => toggleConversion(index)}
              >
                Scientific
              </button>
            </div>
            <div className="bg-gray-100 h-12 flex items-center justify-center rounded text-blue-700 font-bold text-base mb-2">
              {unitId ? (
                <>
                  {formatNumber(getConvertedValue(unitId), conversionToggles[index])}{" "}
                  {units.find((u) => u.id === unitId)?.symbol}
                </>
              ) : (
                "..."
              )}
            </div>
            <div className="overflow-y-auto max-h-32 border rounded px-2 py-1 text-sm space-y-1">
              {units.map((u) => (
                <div
                  key={u.id}
                  onClick={() => {
                    const newSelection = [...selectedUnits];
                    newSelection[index] = u.id;
                    setSelectedUnits(newSelection);
                  }}
                  className={`cursor-pointer p-1 rounded hover:bg-blue-100 ${
                    selectedUnits[index] === u.id ? "bg-blue-200 font-medium" : ""
                  }`}
                >
                  {u.name} ({u.symbol}) â€” {formatNumber(getConvertedValue(u.id), conversionToggles[index])}
                </div>
              ))}
            </div>
          </div>

          {/* Comparison Box */}
          <div className="bg-white p-4 rounded shadow mt-4">
            <div className="text-center text-lg font-semibold mb-2">Comparison</div>
            <div className="flex justify-center gap-2 mb-2">
              <button
                className={`px-4 py-1 rounded-l ${!comparisonToggles[index] ? "bg-blue-600 text-white" : "bg-white border"}`}
                onClick={() => toggleComparison(index)}
              >
                General
              </button>
              <button
                className={`px-4 py-1 rounded-r ${comparisonToggles[index] ? "bg-blue-600 text-white" : "bg-white border"}`}
                onClick={() => toggleComparison(index)}
              >
                Scientific
              </button>
            </div>
            <div className="bg-gray-100 h-12 flex items-center justify-center rounded text-blue-700 font-bold text-base mb-4">
              {selectedItems[index] && inputValue ? (
                <>
                  {formatNumber(getComparisonValue(index), comparisonToggles[index])}{" "}
                  {selectedItems[index]?.expand?.unit?.symbol || ""}
                </>
              ) : (
                "..."
              )}
            </div>
            <RealWorldBox
              selected={selectedItems[index]}
              setSelected={(item) => {
                const updated = [...selectedItems];
                updated[index] = item;
                setSelectedItems(updated);
              }}
              items={realWorldItems}
              scientificToggle={true}
            />
          </div>
        </div>
      ))}
    </div>
  );
}

export default Converter;
