// Converter.jsx
import React, { useState, useEffect } from "react";
import pb from "../utils/pocketbaseClient";
import { formatNumber } from "../utils/formatNumber";

function Converter({ selectedCategory }) {
  const [units, setUnits] = useState([]);
  const [realItems, setRealItems] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [fromUnitId, setFromUnitId] = useState("");
  const [toUnitIds, setToUnitIds] = useState(["", "", ""]);
  const [selectedRealItems, setSelectedRealItems] = useState([null, null, null]);
  const [scientificToggles, setScientificToggles] = useState([false, false, false, false, false, false]);

  useEffect(() => {
    async function fetchData() {
      const categories = await pb.collection("categories").getFullList();
      const category = categories.find((c) => c.name === selectedCategory);
      if (!category) return;

      const units = await pb.collection("units").getFullList({ filter: `category = "${category.id}"` });
      const real = await pb.collection("realworld_items").getFullList({
        filter: `category = "${category.id}"`,
        expand: "unit",
      });

      const sortedUnits = units.sort((a, b) => a.to_base_factor - b.to_base_factor);
      const sortedReal = real.sort((a, b) => a.scientific_value - b.scientific_value);

      setUnits(sortedUnits);
      setRealItems(sortedReal);
      setFromUnitId(sortedUnits[0]?.id || "");
      setToUnitIds(sortedUnits.slice(1, 4).map((u) => u.id));
      setSelectedRealItems(sortedReal.slice(0, 3));
    }

    if (selectedCategory) fetchData();
  }, [selectedCategory]);

  const convertToBase = () => {
    const from = units.find((u) => u.id === fromUnitId);
    return from && inputValue ? parseFloat(inputValue) * from.to_base_factor : 0;
  };

  const baseValue = convertToBase();

  const renderUnitBox = (unitId, index) => {
    const unit = units.find((u) => u.id === unitId);
    const result = unit ? baseValue / unit.to_base_factor : null;

    return (
      <div className="bg-white border rounded p-4 shadow flex flex-col gap-2 w-full">
        <div className="text-blue-600 text-xl font-bold text-center">
          {formatNumber(result, scientificToggles[index])}
        </div>
        <select
          value={unitId}
          onChange={(e) => {
            const updated = [...toUnitIds];
            updated[index] = e.target.value;
            setToUnitIds(updated);
          }}
          className="border p-2 rounded w-full text-sm"
        >
          {units.map((u) => (
            <option key={u.id} value={u.id}>
              {u.name} ({u.symbol})
            </option>
          ))}
        </select>
        <div className="flex justify-center gap-2 mt-2">
          <button
            onClick={() => {
              const toggles = [...scientificToggles];
              toggles[index] = false;
              setScientificToggles(toggles);
            }}
            className={`px-3 py-1 rounded-l ${!scientificToggles[index] ? "bg-blue-600 text-white" : "bg-gray-100 border"}`}
          >
            General
          </button>
          <button
            onClick={() => {
              const toggles = [...scientificToggles];
              toggles[index] = true;
              setScientificToggles(toggles);
            }}
            className={`px-3 py-1 rounded-r ${scientificToggles[index] ? "bg-blue-600 text-white" : "bg-gray-100 border"}`}
          >
            Scientific
          </button>
        </div>
      </div>
    );
  };

  const renderRealCard = (item, index) => {
    const result = item && inputValue && item.scientific_value ? baseValue / item.scientific_value : null;

    return (
      <div className="bg-white border rounded p-4 shadow flex flex-col gap-2 w-full">
        <div className="text-blue-600 text-xl font-bold text-center break-words">
          {formatNumber(result, scientificToggles[index + 3])}
        </div>
        <select
          value={item?.id || ""}
          onChange={(e) => {
            const selected = realItems.find((r) => r.id === e.target.value);
            const updated = [...selectedRealItems];
            updated[index] = selected;
            setSelectedRealItems(updated);
          }}
          className="border p-2 rounded w-full text-sm"
        >
          {realItems.map((r) => (
            <option key={r.id} value={r.id}>
              {r.name}
            </option>
          ))}
        </select>
        {item && (
          <div className="bg-gray-50 border rounded p-2 text-sm">
            <div className="text-center font-semibold">{item.name}</div>
            <div className="flex justify-between text-xs mt-1">
              <span className="text-blue-600">{formatNumber(item.scientific_value, true)}</span>
              <span>{formatNumber(item.approx_value, false, true)} {item.expand?.unit?.symbol || ""}</span>
            </div>
            {item.notes && <div className="text-center italic text-gray-500 mt-2">{item.notes}</div>}
          </div>
        )}
        <div className="flex justify-center gap-2 mt-2">
          <button
            onClick={() => {
              const toggles = [...scientificToggles];
              toggles[index + 3] = false;
              setScientificToggles(toggles);
            }}
            className={`px-3 py-1 rounded-l ${!scientificToggles[index + 3] ? "bg-blue-600 text-white" : "bg-gray-100 border"}`}
          >
            General
          </button>
          <button
            onClick={() => {
              const toggles = [...scientificToggles];
              toggles[index + 3] = true;
              setScientificToggles(toggles);
            }}
            className={`px-3 py-1 rounded-r ${scientificToggles[index + 3] ? "bg-blue-600 text-white" : "bg-gray-100 border"}`}
          >
            Scientific
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className="bg-white rounded shadow p-6 mb-8">
      <h2 className="text-xl font-bold text-blue-700 text-center mb-4">{selectedCategory} Converter</h2>

      {/* Input & Dropdown */}
      <div className="grid sm:grid-cols-2 gap-4 mb-6">
        <input
          type="number"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          className="border p-2 rounded w-full"
          placeholder="Enter value"
        />
        <select
          value={fromUnitId}
          onChange={(e) => setFromUnitId(e.target.value)}
          className="border p-2 rounded w-full"
        >
          {units.map((u) => (
            <option key={u.id} value={u.id}>
              {u.name} ({u.symbol})
            </option>
          ))}
        </select>
      </div>

      {/* Unit Conversion Section */}
      <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 mb-6">
        {toUnitIds.map((unitId, idx) => renderUnitBox(unitId, idx))}
      </div>

      {/* Real-World Comparison Section */}
      <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
        {selectedRealItems.map((item, idx) => renderRealCard(item, idx))}
      </div>
    </div>
  );
}

export default Converter;
