// Converter.jsx
import React, { useState, useEffect } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber } from "../utils/formatNumber";

function Converter({ categoryName }) {
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [selectedUnit, setSelectedUnit] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [selectedRealItems, setSelectedRealItems] = useState([null, null, null]);
  const [categoryId, setCategoryId] = useState("");
  const [conversionToggles, setConversionToggles] = useState([false, false, false]);
  const [comparisonToggles, setComparisonToggles] = useState([false, false, false]);

  useEffect(() => {
    async function fetchCategory() {
      const categories = await pb.collection("categories").getFullList();
      const match = categories.find((c) => c.name === categoryName);
      if (match) setCategoryId(match.id);
    }
    if (categoryName) fetchCategory();
  }, [categoryName]);

  useEffect(() => {
    async function fetchData() {
      const unitList = await pb.collection("units").getFullList({
        filter: `category = "${categoryId}"`,
      });
      const sortedUnits = unitList.sort((a, b) => a.to_base_factor - b.to_base_factor);
      setUnits(sortedUnits);
      setSelectedUnit(sortedUnits[0]?.id || "");

      const realItems = await pb.collection("realworld_items").getFullList({
        filter: `category = "${categoryId}"`,
        expand: "unit",
      });
      const sortedReal = realItems.sort((a, b) => a.scientific_value - b.scientific_value);
      setRealWorldItems(sortedReal);
      setSelectedRealItems([sortedReal[0], sortedReal[1], sortedReal[2]]);
    }
    if (categoryId) fetchData();
  }, [categoryId]);

  const convertedValue = (() => {
    const from = units.find((u) => u.id === selectedUnit);
    return from && inputValue ? parseFloat(inputValue) * from.to_base_factor : null;
  })();

  const toggle = (index, type) => {
    const arr = type === "conversion" ? [...conversionToggles] : [...comparisonToggles];
    arr[index] = !arr[index];
    type === "conversion" ? setConversionToggles(arr) : setComparisonToggles(arr);
  };

  const updateRealItem = (index, item) => {
    const updated = [...selectedRealItems];
    updated[index] = item;
    setSelectedRealItems(updated);
  };

  return (
    <div className="flex flex-col lg:flex-row gap-6 bg-white p-6 rounded shadow">
      {/* From Block */}
      <div className="lg:w-1/3">
        <h2 className="text-xl font-bold mb-2">From</h2>
        <input
          type="number"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          className="border p-2 rounded w-full mb-2"
          placeholder="Enter value"
        />
        <select
          value={selectedUnit}
          onChange={(e) => setSelectedUnit(e.target.value)}
          className="border p-2 rounded w-full"
          size={5}
        >
          {units.map((u) => (
            <option key={u.id} value={u.id}>
              {u.name} ({u.symbol})
            </option>
          ))}
        </select>
      </div>

      {/* Conversion and Comparison Block */}
      <div className="lg:w-2/3 flex flex-col gap-8">
        {/* Conversion Boxes */}
        <div>
          <h3 className="text-lg font-semibold text-center mb-4">Unit Conversion</h3>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
            {[0, 1, 2].map((i) => {
              const unit = units[i];
              if (!unit || !convertedValue) return null;
              const converted = convertedValue / unit.to_base_factor;
              return (
                <div key={i}>
                  <div className="flex justify-center gap-2 mb-2">
                    <button
                      className={`px-4 py-1 rounded-l ${!conversionToggles[i] ? "bg-blue-600 text-white" : "bg-white border"}`}
                      onClick={() => toggle(i, "conversion")}
                    >
                      General
                    </button>
                    <button
                      className={`px-4 py-1 rounded-r ${conversionToggles[i] ? "bg-blue-600 text-white" : "bg-white border"}`}
                      onClick={() => toggle(i, "conversion")}
                    >
                      Scientific
                    </button>
                  </div>
                  <div className="bg-gray-100 border rounded p-4 text-center">
                    <div className="text-sm font-semibold text-gray-600">{unit.name}</div>
                    <div className="text-xl font-bold text-blue-700 my-2 break-words">
                      {formatNumber(converted, conversionToggles[i])} {unit.symbol}
                    </div>
                    <select
                      className="border p-2 rounded w-full text-sm"
                      size={5}
                      value={unit.id}
                      onChange={(e) => {
                        const newUnit = units.find((u) => u.id === e.target.value);
                        if (newUnit) {
                          const updated = [...units];
                          updated[i] = newUnit;
                          setUnits(updated);
                        }
                      }}
                    >
                      {units.map((u) => (
                        <option key={u.id} value={u.id}>
                          {u.name} ({u.symbol})
                        </option>
                      ))}
                    </select>
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        {/* Real World Comparison */}
        <div>
          <h3 className="text-lg font-semibold text-center mb-4">Real-World Comparison</h3>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
            {[0, 1, 2].map((i) => (
              <div key={i}>
                <div className="flex justify-center gap-2 mb-2">
                  <button
                    className={`px-4 py-1 rounded-l ${!comparisonToggles[i] ? "bg-blue-600 text-white" : "bg-white border"}`}
                    onClick={() => toggle(i, "comparison")}
                  >
                    General
                  </button>
                  <button
                    className={`px-4 py-1 rounded-r ${comparisonToggles[i] ? "bg-blue-600 text-white" : "bg-white border"}`}
                    onClick={() => toggle(i, "comparison")}
                  >
                    Scientific
                  </button>
                </div>
                <RealWorldBox
                  selected={selectedRealItems[i]}
                  setSelected={(item) => updateRealItem(i, item)}
                  items={realWorldItems}
                  inputValue={convertedValue}
                  scientific={comparisonToggles[i]}
                />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Converter;
