import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { useTheme } from "../context/ThemeContext";
import { formatNumber } from "../utils/formatNumber";

function parseScientific(val) {
  if (val == null) return null;
  if (typeof val === "number") return val;
  if (typeof val === "object" && val.value !== undefined) return val.value;
  const num = Number(val);
  return isNaN(num) ? null : num;
}

export default function MoleConverter({ categoryId }) {
  const [inputValue, setInputValue] = useState("");
  const [fromUnit, setFromUnit] = useState(null);
  const [convertedValues, setConvertedValues] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [units, setUnits] = useState([]);
  const [childUnits, setChildUnits] = useState([]);
  const [comparisonItems, setComparisonItems] = useState([null, null, null]);
  const [displayScientific, setDisplayScientific] = useState([false, false, false]);
  const [displayComparisonScientific, setDisplayComparisonScientific] = useState([false, false, false]);

  const theme = useTheme();

  useEffect(() => {
    if (!categoryId) return;
    const fetchUnits = async () => {
      const unitData = await pb.collection("units").getFullList({ filter: `category="${categoryId}"` });
      setUnits(unitData);
      setFromUnit(unitData.find((unit) => unit.is_base) || unitData[0]);
    };

    const fetchRealWorldItems = async () => {
      const data = await pb.collection("real_world").getFullList({ filter: `category="${categoryId}"` });
      setRealWorldItems(data);
      setComparisonItems(data.slice(0, 3));
    };

    fetchUnits();
    fetchRealWorldItems();
  }, [categoryId]);

  useEffect(() => {
    if (!fromUnit) return;
    const children = units.filter((unit) => unit.parent_unit === fromUnit.id);
    setChildUnits(children);
  }, [fromUnit, units]);

  useEffect(() => {
    if (!fromUnit || inputValue === "") {
      setConvertedValues([]);
      return;
    }

    const baseValue = parseScientific(inputValue) * parseScientific(fromUnit.to_base_factor || 1);

    const conversions = childUnits.map((unit) => {
      const factor = parseScientific(unit.to_base_factor || 1);
      const value = baseValue / factor;
      return { ...unit, value };
    });

    setConvertedValues(conversions);
  }, [inputValue, fromUnit, childUnits]);

  const handleComparisonChange = (index, item) => {
    const newItems = [...comparisonItems];
    newItems[index] = item;
    setComparisonItems(newItems);
  };

  return (
    <div className="w-full flex flex-col gap-6">
      {/* Input Section */}
      <div className="flex flex-col md:flex-row items-center gap-4">
        <input
          type="number"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="Enter value"
          className="border px-4 py-2 rounded w-full md:w-1/3"
        />
        <select
          value={fromUnit?.id || ""}
          onChange={(e) => {
            const selected = units.find((u) => u.id === e.target.value);
            setFromUnit(selected);
          }}
          className="border px-4 py-2 rounded w-full md:w-1/3"
        >
          {units.map((unit) => (
            <option key={unit.id} value={unit.id}>
              {unit.name}
            </option>
          ))}
        </select>
      </div>

      {/* Conversion Output Boxes */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {[0, 1, 2].map((i) => {
          const unit = convertedValues[i];
          if (!unit) return <div key={i} className="h-40 bg-gray-100 rounded" />;
          return (
            <div
              key={i}
              className="p-4 rounded shadow"
              style={{ backgroundColor: theme?.surface || "#f3f4f6" }}
            >
              <div className="text-xl font-semibold mb-2">
                {displayScientific[i]
                  ? Number(unit.value).toExponential(3)
                  : formatNumber(unit.value)}{" "}
                {unit.symbol}
              </div>
              <select
                value={unit.id}
                onChange={(e) => {
                  const newUnit = convertedValues.find((u) => u.id === e.target.value);
                  const newConverted = [...convertedValues];
                  newConverted[i] = newUnit;
                  setConvertedValues(newConverted);
                }}
                className="w-full mt-2 border rounded px-2 py-1"
              >
                {convertedValues.map((u) => (
                  <option key={u.id} value={u.id}>
                    {u.name}
                  </option>
                ))}
              </select>
              <button
                onClick={() => {
                  const updated = [...displayScientific];
                  updated[i] = !updated[i];
                  setDisplayScientific(updated);
                }}
                className="mt-2 text-sm text-blue-500 underline"
              >
                {displayScientific[i] ? "Show General" : "Show Scientific"}
              </button>
            </div>
          );
        })}
      </div>

      {/* Real World Comparison */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {comparisonItems.map((item, index) => (
          <RealWorldBox
            key={index}
            index={index}
            inputValue={inputValue}
            fromUnit={fromUnit}
            allItems={realWorldItems}
            selectedItem={item}
            onChange={handleComparisonChange}
            scientific={displayComparisonScientific[index]}
            onToggleScientific={() => {
              const updated = [...displayComparisonScientific];
              updated[index] = !updated[index];
              setDisplayComparisonScientific(updated);
            }}
          />
        ))}
      </div>
    </div>
  );
}
