import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber } from "../utils/formatNumber";
import { useTheme } from "../context/ThemeContext";

function parseScientific(val) {
  if (val == null) return null;
  if (typeof val === "number") return val;
  if (typeof val === "object") return parseFloat(val?.value || 0);
  return parseFloat(val);
}

function Converter({ category }) {
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [selectedUnit, setSelectedUnit] = useState(null);
  const [convertedValues, setConvertedValues] = useState([]);
  const [selectedRealWorld, setSelectedRealWorld] = useState([null, null, null]);
  const [displayScientific, setDisplayScientific] = useState([false, false, false]);
  const theme = useTheme();

  useEffect(() => {
    if (!category?.id) return;

    const fetchData = async () => {
      try {
        const [unitRes, realWorldRes] = await Promise.all([
          pb.collection("units").getFullList({ filter: `category="${category.id}"` }),
          pb.collection("realworld_items").getFullList({
            filter: `category="${category.id}"`,
            sort: "sort_priority", // Ascending sort
          }),
        ]);

        const sortedItems = realWorldRes.sort(
          (a, b) => (a.sort_priority ?? 999) - (b.sort_priority ?? 999)
        );

        setUnits(unitRes);
        setRealWorldItems(sortedItems);
        setSelectedUnit(unitRes[0] || null);
        setSelectedRealWorld([
          sortedItems[0] || null,
          sortedItems[1] || null,
          sortedItems[2] || null,
        ]);
      } catch (error) {
        console.error("Failed to fetch units or real-world items:", error);
      }
    };

    fetchData();
  }, [category]);

  useEffect(() => {
    if (!inputValue || !selectedUnit || units.length === 0) {
      setConvertedValues([]);
      return;
    }

    const inputBaseValue = parseScientific(inputValue) * parseScientific(selectedUnit.to_base_factor);

    const values = units.map((unit) => {
      const converted = inputBaseValue / parseScientific(unit.to_base_factor);
      return {
        unit,
        value: converted,
      };
    });

    setConvertedValues(values);
  }, [inputValue, selectedUnit, units]);

  const handleToggle = (index) => {
    setDisplayScientific((prev) => {
      const updated = [...prev];
      updated[index] = !updated[index];
      return updated;
    });
  };

  return (
    <div className="p-4">
      <div className="mb-4">
        <label className="block font-medium mb-1 text-gray-700">Enter value:</label>
        <input
          type="number"
          className="w-full border rounded p-2"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
      </div>

      <div className="mb-6">
        <label className="block font-medium mb-1 text-gray-700">Select Unit:</label>
        <select
          className="w-full border rounded p-2"
          value={selectedUnit?.id || ""}
          onChange={(e) => {
            const found = units.find((u) => u.id === e.target.value);
            if (found) setSelectedUnit(found);
          }}
        >
          {units.map((unit) => (
            <option key={unit.id} value={unit.id}>
              {unit.name}
            </option>
          ))}
        </select>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {[0, 1, 2].map((index) => {
          const item = selectedRealWorld[index];
          if (!item) return <div key={index}>No item</div>;

          const itemValue = parseScientific(item.value);
          const inputBase = parseScientific(inputValue) * parseScientific(selectedUnit?.to_base_factor);
          const ratio = inputBase / itemValue;

          const formatted = displayScientific[index]
            ? ratio.toExponential(3)
            : formatNumber(ratio);

          return (
            <div key={index} className="border rounded p-4 bg-white shadow">
              <div className="mb-2 text-gray-700 font-semibold">{item.name}</div>
              <div className="text-xl mb-1">{formatted}</div>
              <div className="text-sm text-gray-600 mb-2">{item.note || ""}</div>
              <select
                className="w-full border rounded p-2"
                value={item.id}
                onChange={(e) => {
                  const selected = realWorldItems.find((rw) => rw.id === e.target.value);
                  if (selected) {
                    setSelectedRealWorld((prev) => {
                      const updated = [...prev];
                      updated[index] = selected;
                      return updated;
                    });
                  }
                }}
              >
                {realWorldItems.map((rw) => (
                  <option key={rw.id} value={rw.id}>
                    {rw.name}
                  </option>
                ))}
              </select>

              <button
                onClick={() => handleToggle(index)}
                className="mt-2 text-sm text-blue-600 underline"
              >
                {displayScientific[index] ? "View General" : "View Scientific"}
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Converter;
