import React, { useEffect, useState } from "react";
import { formatNumber } from "../utils/formatNumber";

const ComparisonBox = ({ items, inputValue, comparisonToggles, setComparisonToggles, theme }) => {
  // Sort once to pick 3 lowest values
  const sortedItems = [...items].sort((a, b) => a.scientific_value - b.scientific_value);

  const [selectedItems, setSelectedItems] = useState([
    sortedItems[0]?.id || "",
    sortedItems[1]?.id || "",
    sortedItems[2]?.id || "",
  ]);

  const getApproxComparison = (item) => {
    if (!item || !item.approx_value || !inputValue) return null;
    return inputValue / item.approx_value;
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
      {selectedItems.map((selectedId, index) => {
        const item = items.find((i) => i.id === selectedId);
        return (
          <div
            key={index}
            className="p-4 rounded shadow flex flex-col gap-3"
            style={{ backgroundColor: theme?.box }}
          >
            {/* Toggle */}
            <div className="flex justify-center gap-2">
              <button
                className={`px-3 py-1 rounded-l ${
                  !comparisonToggles[index] ? "bg-[#FEB73F] text-white" : "bg-white border text-black"
                }`}
                onClick={() =>
                  setComparisonToggles((prev) =>
                    prev.map((val, i) => (i === index ? false : val))
                  )
                }
              >
                General
              </button>
              <button
                className={`px-3 py-1 rounded-r ${
                  comparisonToggles[index] ? "bg-[#FEB73F] text-white" : "bg-white border text-black"
                }`}
                onClick={() =>
                  setComparisonToggles((prev) =>
                    prev.map((val, i) => (i === index ? true : val))
                  )
                }
              >
                Scientific
              </button>
            </div>

            {/* Output */}
            <div className="bg-gray-100 p-3 rounded text-center text-green-700 font-bold text-base min-h-[48px]">
              {formatNumber(getApproxComparison(item), comparisonToggles[index])}
            </div>

            {/* Dropdown */}
            <div className="border rounded max-h-36 overflow-y-auto text-sm bg-white">
              {sortedItems.map((obj) => (
                <div
                  key={obj.id}
                  className={`cursor-pointer p-1 hover:bg-green-100 ${
                    obj.id === selectedId ? "bg-green-200 font-medium" : ""
                  }`}
                  onClick={() =>
                    setSelectedItems((prev) =>
                      prev.map((val, i) => (i === index ? obj.id : val))
                    )
                  }
                >
                  {obj.name}
                  <div className="text-xs text-gray-600">
                    {formatNumber(obj.scientific_value, true)} (Scientific)
                  </div>
                  <div className="text-xs text-gray-500">
                    {formatNumber(obj.approx_value)} (General)
                  </div>
                  {obj.notes && (
                    <div className="text-[10px] text-gray-400 italic">{obj.notes}</div>
                  )}
                </div>
              ))}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default ComparisonBox;
