// src/pages/CategoryPage.jsx
import React from "react";
import AuxiliaryConverter from "../components/AuxiliaryConverter";
import Converter from "../components/Converter";
import FunFacts from "../components/FunFacts";

export default function CategoryPage({ categoryName }) {
  return (
    <div className="bg-white p-6 rounded shadow-md">
      <h2 className="text-2xl font-bold text-blue-600 mb-4">
        {categoryName} Converter
      </h2>

      <Converter category={categoryName} />

      {/* Optional: Render additional components for more complex categories */}
      <AuxiliaryConverter category={categoryName} />
      <FunFacts category={categoryName} />
    </div>
  );
}
