import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber } from "../utils/formatNumber";
import { useTheme } from "../context/ThemeContext";

function parseScientific(val) {
  if (val == null) return null;
  if (typeof val === "number") return val;
  if (typeof val === "object" && "mantissa" in val && "exponent" in val) {
    return val.mantissa * Math.pow(10, val.exponent);
  }
  const num = Number(val);
  return isNaN(num) ? null : num;
}

function Converter({ categoryId }) {
  const theme = useTheme();
  const [units, setUnits] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [fromUnit, setFromUnit] = useState("");
  const [selectedUnits, setSelectedUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [selectedItems, setSelectedItems] = useState([null, null, null]);
  const [comparisonToggles, setComparisonToggles] = useState([false, false, false]);
  const [conversionToggles, setConversionToggles] = useState([false, false, false]);
  const [categoryInfo, setCategoryInfo] = useState(null);
  const [childUnits, setChildUnits] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [unitList, category] = await Promise.all([
          pb.collection("units").getFullList({
            filter: `category = "${categoryId}"`,
          }),
          pb.collection("categories").getOne(categoryId),
        ]);

        setCategoryInfo(category);
        const sortedUnits = unitList.sort((a, b) => a.to_base_factor - b.to_base_factor);
        setUnits(sortedUnits);

        const topUnits = sortedUnits.filter((u) => !u.parent_unit);
        const initialFrom = topUnits[0]?.id || "";
        setFromUnit(initialFrom);
      } catch (err) {
        console.error("Fetch error:", err);
      }
    };

    if (categoryId) fetchData();
  }, [categoryId]);

  useEffect(() => {
    const fetchChildUnits = async () => {
      if (!fromUnit || !categoryInfo) return;

      const isMole = categoryInfo.name.toLowerCase() === "mole";

      if (isMole) {
        const [childs, items] = await Promise.all([
          pb.collection("units").getFullList({ filter: `parent_unit = "${fromUnit}"` }),
          pb.collection("realworld_items").getFullList({ filter: `unit.parent_unit = "${fromUnit}"`, expand: "unit" }),
        ]);

        const sortedChilds = childs.sort((a, b) => a.to_base_factor - b.to_base_factor);
        const sortedItems = items.sort((a, b) => parseScientific(a.scientific_value) - parseScientific(b.scientific_value));

        setChildUnits(sortedChilds);
        setSelectedUnits(sortedChilds.slice(0, 3).map((u) => u.id));
        setRealWorldItems(sortedItems);
        setSelectedItems([sortedItems[0] || null, sortedItems[1] || null, sortedItems[2] || null]);
      } else {
        const sorted = units.sort((a, b) => a.to_base_factor - b.to_base_factor);
        const others = sorted.filter((u) => u.id !== fromUnit);

        const [items] = await Promise.all([
          pb.collection("realworld_items").getFullList({ filter: `category = "${categoryId}"`, expand: "unit" }),
        ]);

        const sortedItems = items.sort((a, b) => parseScientific(a.scientific_value) - parseScientific(b.scientific_value));

        setChildUnits(others);
        setSelectedUnits(others.slice(0, 3).map((u) => u.id));
        setRealWorldItems(sortedItems);
        setSelectedItems([sortedItems[0] || null, sortedItems[1] || null, sortedItems[2] || null]);
      }
    };

    fetchChildUnits();
  }, [fromUnit, categoryInfo]);

  const getConvertedValue = (toUnitId) => {
    const from = units.find((u) => u.id === fromUnit);
    const to = childUnits.find((u) => u.id === toUnitId);
    if (!from || !to || !inputValue) return null;
    const input = parseFloat(inputValue);
    const baseValue = input * from.to_base_factor;
    return baseValue / to.to_base_factor;
  };

  const getComparisonValue = (item) => {
    const from = units.find((u) => u.id === fromUnit);
    const approx = item?.approx_value;
    if (!from || !approx || !inputValue) return null;

    const input = parseFloat(inputValue);
    const baseValue = input * from.to_base_factor;
    return baseValue / approx;
  };

  return (
    <div className="space-y-10">
      {categoryInfo?.top_notes && (
        <div className="bg-yellow-100 border-l-4 border-yellow-500 p-4 rounded text-sm text-gray-800 mb-6"
             dangerouslySetInnerHTML={{ __html: categoryInfo.top_notes }} />
      )}

      <div className="flex flex-col lg:flex-row gap-6">
        <div className="w-full lg:w-64 flex flex-col items-center justify-center gap-4">
          <label className="font-semibold text-lg">From</label>
          <input
            type="number"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            placeholder="Enter value"
            className="border p-2 rounded w-full"
            style={{ backgroundColor: theme?.base_color, color: theme?.text }}
          />
          <div className="border rounded max-h-40 overflow-y-auto w-full text-sm space-y-1"
               style={{ backgroundColor: theme?.base_color }}>
            {units.filter((u) => !u.parent_unit).map((u) => (
              <div
                key={u.id}
                className={`cursor-pointer p-1 rounded hover:bg-blue-100 ${fromUnit === u.id ? "bg-blue-200 font-medium" : ""}`}
                onClick={() => setFromUnit(u.id)}
              >
                {u.name} ({u.symbol})
              </div>
            ))}
          </div>
        </div>

        <div className="flex-1 space-y-10">
          {/* Conversion */}
          {units.length > 1 && childUnits.length > 0 && (
            <div>
              <div className="text-center text-xl font-bold mb-2" style={{ color: theme?.text }}>
                Conversion
              </div>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                {selectedUnits.map((toUnitId, index) => {
                  const currentUnit = childUnits.find((u) => u.id === toUnitId);
                  return (
                    <div key={index} className="p-4 rounded shadow flex flex-col gap-3"
                         style={{ backgroundColor: theme?.base_color }}>
                      {/* Conversion Box Content */}
                      ...
                    </div>
                  );
                })}
              </div>
            </div>
          )}

          {/* Comparison */}
          {realWorldItems.length > 0 && (
            <div>
              <div className="text-center text-xl font-bold mb-2" style={{ color: theme?.text }}>
                Comparison
              </div>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                {selectedItems.map((selectedItem, index) => (
                  <div key={index} className="p-4 rounded shadow flex flex-col gap-3"
                       style={{ backgroundColor: theme?.base_color }}>
                    {/* Comparison Box Content */}
                    ...
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Converter;
