// App.js
import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import CategoryPage from "./pages/CategoryPage";

const categories = [
  "Acceleration", "Angular Acceleration", "Angular Velocity",
  "Area", "Capacitance", "Charge", "Current", "Density", "Electric Field",
  "Energy", "Force", "Frequency", "Illuminance", "Length",
  "Magnetic Flux Density", "Mass", "Moment of Inertia", "Power",
  "Pressure", "Resistance", "Resistivity", "Specific Heat", "Speed",
  "Surface Tension", "Thermal Conductivity", "Temperature", "Time",
  "Torque", "Viscosity", "Volume", "Voltage", "Young's Modulus"
];

function Home() {
  return (
    <main className="flex-grow p-6">
      <h2 className="text-xl font-semibold text-center mb-6">
        Select a Conversion Category
      </h2>
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4 mb-8">
        {categories.map((cat) => (
          <Link
            key={cat}
            to={`/category/${encodeURIComponent(cat)}`}
            className="bg-white shadow hover:bg-blue-50 text-gray-800 font-medium border border-gray-200 rounded px-4 py-3 text-center block"
          >
            {cat}
          </Link>
        ))}
      </div>
    </main>
  );
}

export default function App() {
  return (
    <Router>
      <div className="min-h-screen flex flex-col bg-gray-100">
        <header className="bg-white shadow py-4 px-6">
          <Link to="/" className="text-2xl font-bold text-blue-600">ðŸ”„ UnitCompare</Link>
        </header>

        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/category/:categoryName" element={<CategoryPage />} />
        </Routes>

        <footer className="bg-white border-t text-center text-sm text-gray-500 py-4 mt-6">
          Â© 2025 UnitCompare. All rights reserved.
        </footer>
      </div>
    </Router>
  );
}
