import React, { useState } from "react";

const units = ["mm", "cm", "m", "km", "mi", "ft", "in"];

function UnitColumn({ fromUnit }) {
  return (
    <div className="space-y-2 border rounded p-2 text-sm">
      {units.map((unit) => (
        <div
          key={unit}
          className={`p-1 rounded ${unit === fromUnit ? "bg-blue-100 font-semibold text-blue-800" : "text-gray-700"}`}
        >
          {unit}
        </div>
      ))}
    </div>
  );
}

function ComparisonColumn({ value, item }) {
  return (
    <div className="border p-2 rounded bg-white shadow-sm text-center">
      <div className="text-blue-600 font-bold mb-1">{value}</div>
      <div className="text-xs text-gray-600">{item}</div>
    </div>
  );
}

export default function StructuredComparison() {
  const [fromValue, setFromValue] = useState(1);
  const [fromUnit, setFromUnit] = useState("km");

  return (
    <div className="max-w-7xl mx-auto p-6">
      <h2 className="text-xl font-bold mb-4 text-center">Unit Comparison Layout</h2>

      {/* Header Row */}
      <div className="grid grid-cols-8 text-center font-semibold mb-2">
        <div className="col-span-2">Convert</div>
        <div>From</div>
        <div className="col-span-2">Convert</div>
        <div className="col-span-3">Compare</div>
      </div>

      {/* Main Grid */}
      <div className="grid grid-cols-8 gap-4 items-start">
        {/* Convert Columns Left */}
        <UnitColumn fromUnit={fromUnit} />
        <UnitColumn fromUnit={fromUnit} />

        {/* From Input */}
        <div className="space-y-3">
          <input
            type="number"
            value={fromValue}
            onChange={(e) => setFromValue(e.target.value)}
            className="border p-2 rounded w-full"
            placeholder="Enter value"
          />
          <select
            value={fromUnit}
            onChange={(e) => setFromUnit(e.target.value)}
            className="border p-2 rounded w-full"
          >
            {units.map((unit) => (
              <option key={unit}>{unit}</option>
            ))}
          </select>
        </div>

        {/* Convert Columns Right */}
        <UnitColumn fromUnit={fromUnit} />
        <UnitColumn fromUnit={fromUnit} />

        {/* Comparison Columns */}
        <ComparisonColumn value="0.2" item="Baby Carrot" />
        <ComparisonColumn value="5.1×10⁵" item="Bacteria" />
        <ComparisonColumn value="1.1×10⁶" item="Toe Nail Length" />
      </div>
    </div>
  );
}
