import React, { createContext, useEffect, useState, useContext } from "react";
import pb from "../utils/pocketbaseClient";

const ThemeContext = createContext();

export const ThemeProvider = ({ children }) => {
  const [theme, setTheme] = useState(null);

  useEffect(() => {
    const fetchTheme = async () => {
      try {
        const record = await pb.collection("site_settings").getFirstListItem("");

        const themeObject = {
          primary: record.primary_color,           // #feb73f → for headings, buttons
          secondary: record.secondary_color,       // #e6e1e1 → optional light gray
          background: record.background_color,     // #ffffff
          surface: record.surface_color,           // #f3f4f6 → section background
          text: record.text_color,                 // #000000
          font: record.font_family || "sans-serif",
          logo: pb.files.getUrl(record, record.logo_url),
          favicon: pb.files.getUrl(record, record.favicon_url),

          // Custom design tokens for consistency
          tabActive: "#2563EB",                    // General/Scientific active
          tabInactive: "#ffffff",                  // General/Scientific inactive
          tabBorder: "#2563EB",                    // Tab border
        };

        setTheme(themeObject);

        // Set favicon
        if (record.favicon_url) {
          const link = document.querySelector("link[rel~='icon']");
          if (link) link.href = pb.files.getUrl(record, record.favicon_url);
        }

        // Set global font
        if (record.font_family) {
          document.body.style.fontFamily = record.font_family;
        }
      } catch (err) {
        console.error("Failed to load theme settings:", err);
      }
    };

    fetchTheme();
  }, []);

  return (
    <ThemeContext.Provider value={theme}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = () => useContext(ThemeContext);
