import React, { useState, useEffect } from "react";
import PocketBase from "pocketbase";
import "./VolumeConverter.css";

const pb = new PocketBase("http://127.0.0.1:8090");
pb.autoCancellation(false);

const SuperscriptFormat = ({ value }) => {
  if (!value || isNaN(value)) return "...";
  const [base, expRaw] = value.toExponential(2).split("e");
  const exp = expRaw.replace("+", "");
  return (
    <>
      {parseFloat(base).toFixed(2)}×10<sup>{exp}</sup>
    </>
  );
};

const RealWorldBox = ({ selected, setSelected, items, volumeM3, scientific }) => {
  const sortedItems = [...items].sort(
    (a, b) => a.scientific_value - b.scientific_value
  );

  const result =
    selected && volumeM3 && selected.scientific_value
      ? volumeM3 / selected.scientific_value
      : null;

  const formatNormal = (val) => {
    if (!val || isNaN(val)) return "...";
    if (val < 0.001 || val > 1e6) {
      return (
        <SuperscriptFormat value={val} />
      );
    } else {
      return val.toLocaleString();
    }
  };

  return (
    <div className="bg-white shadow-lg border rounded-lg p-4 flex flex-col gap-3">
      {/* Output */}
      <div className="output-value text-blue-600">
        {result
          ? scientific
            ? <SuperscriptFormat value={result} />
            : parseFloat(result).toLocaleString()
          : "..."}
      </div>

      {/* Dropdown */}
      <select
        value={selected?.id || ""}
        onChange={(e) => {
          const item = items.find((obj) => obj.id === e.target.value);
          if (item) setSelected(item);
        }}
        className="border p-2 rounded w-full text-sm"
      >
        {sortedItems.map((item) => (
          <option key={item.id} value={item.id}>
            {item.name}
          </option>
        ))}
      </select>

      {/* Card Body */}
      {selected && (
        <div className="text-sm text-gray-700 bg-gray-50 rounded p-2 border border-gray-200">
          <div className="font-semibold text-center">{selected.name}</div>
          <div className="flex justify-between text-xs mt-1">
            <span className="text-blue-600">
              <SuperscriptFormat value={selected.scientific_value} />
            </span>
            <span className="text-right text-gray-800">
              {formatNormal(selected.approx_value)} {selected.unit}
            </span>
          </div>
          {selected.notes && (
            <div className="text-center text-gray-500 italic mt-2">
              {selected.notes}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default function VolumeConverter() {
  const [units, setUnits] = useState([]);
  const [realItems, setRealItems] = useState([]);
  const [fromUnit, setFromUnit] = useState("");
  const [val, setVal] = useState("");
  const [catId, setCatId] = useState("");
  const [to1, setTo1] = useState("");
  const [to2, setTo2] = useState("");
  const [to3, setTo3] = useState("");
  const [useSci, setUseSci] = useState(false);
  const [real1, setReal1] = useState(null);
  const [real2, setReal2] = useState(null);
  const [real3, setReal3] = useState(null);

  useEffect(() => {
    const fetchCat = async () => {
      const cats = await pb.collection("categories").getFullList();
      const vol = cats.find((c) => c.name === "Volume");
      if (vol) setCatId(vol.id);
    };
    fetchCat();
  }, []);

  useEffect(() => {
    if (catId) {
      pb.collection("units")
        .getFullList({ filter: `category = "${catId}"` })
        .then(setUnits);
      pb.collection("realworld_items")
        .getFullList({ filter: `category = "${catId}"`, expand: "unit" })
        .then((data) => {
          setRealItems(data);
          setReal1(data[0]);
          setReal2(data[1]);
          setReal3(data[2]);
        });
    }
  }, [catId]);

  const toM3 = () => {
    const from = units.find((u) => u.id === fromUnit);
    return from && val ? parseFloat(val) * from.to_base_factor : null;
  };

  const getVal = (toId) => {
    if (!val || !fromUnit || !toId) return "...";
    const from = units.find((u) => u.id === fromUnit);
    const to = units.find((u) => u.id === toId);
    if (!from || !to) return "...";
    const res = (parseFloat(val) * from.to_base_factor) / to.to_base_factor;
    return useSci ? <SuperscriptFormat value={res} /> : res.toLocaleString();
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-6 bg-white rounded-lg shadow">
      {/* Toggle */}
      <div className="flex justify-end mb-4">
        <div className="flex items-center border rounded bg-white shadow-sm">
          <button
            className={`px-4 py-1 rounded-l ${
              !useSci ? "bg-blue-600 text-white" : "bg-white"
            }`}
            onClick={() => setUseSci(false)}
          >
            Normal
          </button>
          <button
            className={`px-4 py-1 rounded-r ${
              useSci ? "bg-blue-600 text-white" : "bg-white"
            }`}
            onClick={() => setUseSci(true)}
          >
            Scientific
          </button>
        </div>
      </div>

      {/* Grid */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 items-start">
        {/* Left Panel */}
        <div className="space-y-4 mt-8">
          <h3 className="text-lg font-semibold text-center">From</h3>
          <input
            type="number"
            value={val}
            onChange={(e) => setVal(e.target.value)}
            className="border p-2 rounded w-full"
            placeholder="Enter value"
          />
          <select
            value={fromUnit}
            onChange={(e) => setFromUnit(e.target.value)}
            className="border p-2 rounded w-full"
          >
            <option value="">-- Select unit --</option>
            {units.map((u) => (
              <option key={u.id} value={u.id}>
                {u.name} ({u.symbol})
              </option>
            ))}
          </select>
        </div>

        {/* Right Panel */}
        <div className="md:col-span-3">
          {/* Conversion Section */}
          <h2 className="text-2xl font-bold mb-4 text-center">Conversion</h2>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 mb-10">
            {[to1, to2, to3].map((id, i) => {
              const setter = i === 0 ? setTo1 : i === 1 ? setTo2 : setTo3;
              return (
                <div key={i} className="bg-white shadow border p-4 rounded">
                  <div className="output-value text-blue-600 mb-2">
                    {getVal(id)}
                  </div>
                  <select
                    value={id}
                    onChange={(e) => setter(e.target.value)}
                    className="border p-2 rounded w-full text-sm"
                  >
                    <option value="">-- Convert To --</option>
                    {units.map((u) => (
                      <option key={u.id} value={u.id}>
                        {u.name} ({u.symbol}) – {getVal(u.id)}
                      </option>
                    ))}
                  </select>
                </div>
              );
            })}
          </div>

          {/* Comparison Section */}
          {toM3() && (
            <>
              <h2 className="text-2xl font-bold mb-4 text-center">
                Comparison
              </h2>
              <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
                {[real1, real2, real3].map((real, i) => {
                  const setter = i === 0 ? setReal1 : i === 1 ? setReal2 : setReal3;
                  return (
                    <RealWorldBox
                      key={i}
                      selected={real}
                      setSelected={setter}
                      items={realItems}
                      volumeM3={toM3()}
                      scientific={useSci}
                    />
                  );
                })}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}
