import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import ConversionBox from "./ConversionBox";
import ComparisonBox from "./ComparisonBox";
import { parseScientific } from "../utils/parseScientific";
import { useTheme } from "../context/ThemeContext";

function Converter({ categoryId, isMole }) {
  const [units, setUnits] = useState([]);
  const [items, setItems] = useState([]);
  const [selectedItems, setSelectedItems] = useState([null, null, null]);
  const [inputValue, setInputValue] = useState(1);
  const [selectedUnit, setSelectedUnit] = useState(null);
  const [showScientific, setShowScientific] = useState(false);
  const { theme } = useTheme();

  // Fetch units and real-world items
  useEffect(() => {
    async function fetchData() {
      try {
        const unitData = await pb.collection("units").getFullList({
          filter: `category="${categoryId}"`,
        });
        const itemData = await pb.collection("real_world_items").getFullList({
          filter: `category="${categoryId}"`,
        });

        const sortedUnits = unitData
          .filter((u) => u.to_base_factor > 0)
          .sort((a, b) => parseScientific(a.to_base_factor) - parseScientific(b.to_base_factor));

        setUnits(sortedUnits);
        setSelectedUnit(sortedUnits[0] || null);
        setItems(itemData);

        // Sort real-world items by scientific_value (ignore 0/null)
        const sortedItems = itemData
          .filter((item) => parseScientific(item.scientific_value) > 0)
          .sort((a, b) => parseScientific(a.scientific_value) - parseScientific(b.scientific_value));

        setSelectedItems([
          sortedItems[0] || null,
          sortedItems[1] || null,
          sortedItems[2] || null,
        ]);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    }

    fetchData();
  }, [categoryId]);

  return (
    <div className="w-full flex flex-col gap-4">
      {/* Conversion Boxes */}
      <div className="flex justify-between gap-4 flex-wrap">
        {[0, 1, 2].map((index) => (
          <ConversionBox
            key={index}
            index={index}
            inputValue={inputValue}
            setInputValue={setInputValue}
            units={units}
            selectedUnit={selectedUnit}
            setSelectedUnit={setSelectedUnit}
            showScientific={showScientific}
            setShowScientific={setShowScientific}
            theme={theme}
          />
        ))}
      </div>

      {/* Comparison Boxes */}
      <div className="flex justify-between gap-4 flex-wrap mt-4">
        {[0, 1, 2].map((index) => (
          <ComparisonBox
            key={index}
            index={index}
            inputValue={inputValue}
            selectedItem={selectedItems[index]}
            setSelectedItem={(item) => {
              const updated = [...selectedItems];
              updated[index] = item;
              setSelectedItems(updated);
            }}
            items={items}
            showScientific={showScientific}
            setShowScientific={setShowScientific}
            theme={theme}
          />
        ))}
      </div>
    </div>
  );
}

export default Converter;
