import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import AuxiliaryConverter from "../components/AuxiliaryConverter";
import Converter from "../components/Converter";
import FunFacts from "../components/FunFacts";
import Header from "../components/Header";
import Footer from "../components/Footer";

function CategoryPage({ categoryName }) {
  const [categoryId, setCategoryId] = useState(null);
  const [auxiliaryCategories, setAuxiliaryCategories] = useState([]);
  const [error, setError] = useState(null);
  const [topNote, setTopNote] = useState("");
  const [prefixes, setPrefixes] = useState([]);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const normalizedSlug = categoryName.toLowerCase();
        const mainCategory = await pb
          .collection("categories")
          .getFirstListItem(`slug="${normalizedSlug}"`, {
            expand: "auxiliary",
          });

        setCategoryId(mainCategory.id);
        setTopNote(mainCategory.top_note || "");

        const auxiliary = Array.isArray(mainCategory.expand?.auxiliary)
          ? mainCategory.expand.auxiliary
          : mainCategory.expand?.auxiliary
          ? [mainCategory.expand.auxiliary]
          : [];

        setAuxiliaryCategories(auxiliary);
        setError(null);
      } catch (err) {
        console.error("Error fetching categories:", err);
        setError("Category not found or error fetching data.");
      }
    };

    if (categoryName) fetchCategories();
  }, [categoryName]);

  useEffect(() => {
    const fetchPrefixes = async () => {
      try {
        const records = await pb.collection("prefixes").getFullList({
          sort: "+order",
        });
        setPrefixes(records);
      } catch (err) {
        console.error("Error fetching prefixes:", err);
      }
    };

    fetchPrefixes();
  }, []);

  if (error) {
    return (
      <div className="text-center py-10 text-red-600 font-semibold">
        {error}
      </div>
    );
  }

  if (!categoryId)
    return <div className="text-center py-10">Loading category...</div>;

  const total = auxiliaryCategories.length;

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      <Header />

      <main className="flex-grow px-6 py-10 max-w-7xl mx-auto space-y-16">
        {/* Auxiliary Calculation Section */}
        {total > 0 && (
          <section>
            <h2 className="text-3xl font-bold mb-6 text-center text-gray-700">
              Auxiliary Calculation
            </h2>
            <div className="grid gap-8 sm:grid-cols-2">
              {auxiliaryCategories.map((aux, index) => {
                const isLastOdd =
                  total % 2 === 1 && index === total - 1 && total > 1;
                const isOnlyOne = total === 1;

                return (
                  <div
                    key={aux.id}
                    className={`bg-white p-6 rounded-lg shadow hover:shadow-lg transition ${
                      isLastOdd || isOnlyOne
                        ? "sm:col-span-2 sm:mx-auto sm:w-1/2"
                        : ""
                    }`}
                  >
                    <h3 className="text-xl font-semibold mb-4 text-center text-gray-800">
                      {aux.name}
                    </h3>
                    <AuxiliaryConverter categoryId={aux.id} />
                  </div>
                );
              })}
            </div>
          </section>
        )}

        {/* Main Calculation Section */}
        <section>
          <h2 className="text-3xl font-bold mb-6 text-center text-gray-700">
            Main Calculation
          </h2>
          <div className="bg-white p-6 rounded-lg shadow space-y-4">
            {topNote && (
              <div
                className="prose max-w-none"
                dangerouslySetInnerHTML={{ __html: topNote }}
              />
            )}
            <Converter categoryId={categoryId} />
          </div>
        </section>

        {/* Fun Facts Section */}
        <section>
          <h2 className="text-3xl font-bold mb-6 text-center text-gray-700">
            Fun Facts
          </h2>
          <div className="bg-white px-6 py-6 rounded-lg shadow">
            <FunFacts categoryId={categoryId} />
          </div>
        </section>

        {/* Prefix Table Section */}
        <section>
          <h2 className="text-3xl font-bold mb-6 text-center text-gray-700">
            Prefixes
          </h2>
          <div className="bg-white rounded-lg shadow overflow-x-auto">
            {/* Desktop Table */}
            <table className="min-w-full divide-y divide-gray-200 text-sm hidden md:table">
              <thead className="bg-gray-100">
                <tr>
                  <th className="px-4 py-2 text-left font-semibold text-gray-700">Name</th>
                  <th className="px-4 py-2 text-left font-semibold text-gray-700">Symbol</th>
                  <th className="px-4 py-2 text-left font-semibold text-gray-700">Multiplier</th>
                  <th className="px-4 py-2 text-left font-semibold text-gray-700">10ⁿ</th>
                  <th className="px-4 py-2 text-left font-semibold text-gray-700">Description</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {prefixes.map((prefix) => (
                  <tr key={prefix.id} className="hover:bg-gray-50">
                    <td className="px-4 py-2 whitespace-nowrap">{prefix.prefix}</td>
                    <td className="px-4 py-2 whitespace-nowrap">{prefix.symbol}</td>
                    <td className="px-4 py-2 whitespace-nowrap">{prefix.multiplier}</td>
                    <td className="px-4 py-2 whitespace-nowrap">
                      {prefix.exponential?.replace(/"/g, "")}
                    </td>
                    <td className="px-4 py-2 whitespace-pre-line">{prefix.description}</td>
                  </tr>
                ))}
              </tbody>
            </table>

            {/* Mobile Table View */}
            <div className="md:hidden divide-y divide-gray-200">
              {prefixes.map((prefix) => (
                <div key={prefix.id} className="p-4 border-b">
                  <div><span className="font-semibold">Name:</span> {prefix.prefix}</div>
                  <div><span className="font-semibold">Symbol:</span> {prefix.symbol}</div>
                  <div><span className="font-semibold">Multiplier:</span> {prefix.multiplier}</div>
                  <div><span className="font-semibold">10ⁿ:</span> {prefix.exponential?.replace(/"/g, "")}</div>
                  <div className="mt-1 whitespace-pre-line">
                    <span className="font-semibold">Description:</span> {prefix.description}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </section>
      </main>

      <Footer />
    </div>
  );
}

export default CategoryPage;
