import React, { useEffect } from "react";
import { formatNumber } from "../utils/formatNumber";

function RealWorldBox({ selected, setSelected, items = [], inputValue, scientific }) {
  // Defensive: ensure items is always an array
  const safeItems = Array.isArray(items) ? items : [];

  useEffect(() => {
    if (!Array.isArray(items)) {
      console.error("RealWorldBox: items prop is not an array:", items);
    }
  }, [items]);

  const sortedItems = [...safeItems].sort((a, b) => a.scientific_value - b.scientific_value);

  return (
    <div className="overflow-y-auto max-h-[300px] space-y-3 pr-1">
      {sortedItems.map((item) => {
        const result =
          item && inputValue && item.scientific_value !== 0
            ? inputValue / item.scientific_value
            : null;

        const isSelected = selected?.id === item.id;

        return (
          <div
            key={item.id}
            onClick={() => setSelected?.(item)}
            role="button"
            tabIndex={0}
            onKeyDown={(e) => {
              if (e.key === "Enter" || e.key === " ") setSelected?.(item);
            }}
            className={`cursor-pointer border p-3 rounded shadow-sm ${
              isSelected ? "bg-blue-50 border-blue-400" : "bg-white"
            }`}
          >
            {/* Line 1: Item Name */}
            <div className="font-semibold text-blue-800 text-center">{item.name}</div>

            {/* Line 2: Values */}
            <div className="text-sm mt-1 flex flex-col text-center">
              <span className="text-lg text-blue-600 font-bold">
                {formatNumber(result, scientific)}
              </span>
              <span className="text-gray-700">
                {formatNumber(item.scientific_value, true)} (
                {formatNumber(parseFloat(item.approx_value), false, true)}{" "}
                {item.expand?.unit?.symbol || ""})
              </span>
            </div>

            {/* Line 3: Notes (optional) */}
            {item.notes && (
              <div className="text-xs text-gray-500 italic mt-2 text-center">{item.notes}</div>
            )}
          </div>
        );
      })}
    </div>
  );
}

export default RealWorldBox;
