import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import { formatNumber } from "../utils/formatNumber";
import { useTheme } from "../context/ThemeContext";
import RealWorldBox from "./RealWorldBox";

function parseScientific(val) {
  if (val === null || val === undefined) return "";
  if (typeof val === "number") return val.toExponential(2);
  if (typeof val === "string" && val.toLowerCase() === "infinity") return "∞";
  return val;
}

function Converter({ categoryId }) {
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [selectedUnits, setSelectedUnits] = useState(["", "", ""]);
  const [comparisonItems, setComparisonItems] = useState(["", "", ""]);
  const [showScientific, setShowScientific] = useState([false, false, false]);
  const [showComparisonScientific, setShowComparisonScientific] = useState([
    false,
    false,
    false,
  ]);
  const { theme } = useTheme();

  useEffect(() => {
    async function fetchUnits() {
      try {
        const records = await pb.collection("units").getFullList({
          filter: `category = "${categoryId}"`,
        });

        const sorted = records
          .filter((u) => u.expression !== "∞")
          .sort((a, b) => {
            const valA = parseFloat(a.value);
            const valB = parseFloat(b.value);
            return valA - valB;
          });

        setUnits(sorted);

        const defaultUnits = sorted.slice(0, 3).map((u) => u.id);
        setSelectedUnits(defaultUnits);
      } catch (error) {
        console.error("Error loading data:", error);
      }
    }

    async function fetchRealWorldItems() {
      try {
        const records = await pb.collection("realworld_items").getFullList({
          filter: `category = "${categoryId}"`,
        });

        const sorted = records
          .filter((i) => i.expression !== "∞")
          .sort((a, b) => {
            const valA = parseFloat(a.value);
            const valB = parseFloat(b.value);
            return valA - valB;
          });

        setRealWorldItems(sorted);

        const defaultComparisons = sorted.slice(0, 3).map((i) => i.id);
        setComparisonItems(defaultComparisons);
      } catch (error) {
        console.error("Error loading data:", error);
      }
    }

    fetchUnits();
    fetchRealWorldItems();
  }, [categoryId]);

  const handleChange = (index, e) => {
    const newSelection = [...selectedUnits];
    newSelection[index] = e.target.value;
    setSelectedUnits(newSelection);
  };

  const handleComparisonChange = (index, e) => {
    const newSelection = [...comparisonItems];
    newSelection[index] = e.target.value;
    setComparisonItems(newSelection);
  };

  const convertValue = (toUnitId) => {
    const input = parseFloat(inputValue);
    if (isNaN(input)) return "";

    const baseUnit = units.find((u) => u.id === selectedUnits[0]);
    const targetUnit = units.find((u) => u.id === toUnitId);
    if (!baseUnit || !targetUnit) return "";

    const baseValue = parseFloat(baseUnit.to_base_factor || 1);
    const targetValue = parseFloat(targetUnit.to_base_factor || 1);

    const result = (input * baseValue) / targetValue;
    return result;
  };

  const compareRealWorld = (realItemId) => {
    const input = parseFloat(inputValue);
    if (isNaN(input)) return "";

    const item = realWorldItems.find((i) => i.id === realItemId);
    if (!item || item.value === "∞") return "";

    const itemValue = parseFloat(item.value);
    if (itemValue === 0) return "";

    return input / itemValue;
  };

  return (
    <div className="grid md:grid-cols-3 gap-4 mt-6">
      {[0, 1, 2].map((i) => {
        const selectedUnit = units.find((u) => u.id === selectedUnits[i]);
        const converted = convertValue(selectedUnits[i]);

        return (
          <div
            key={i}
            className="rounded-2xl p-4 shadow-md"
            style={{ backgroundColor: theme?.converterBoxColor }}
          >
            <div className="flex justify-between mb-2">
              <p className="font-semibold text-sm text-gray-600">
                Output {i + 1}
              </p>
              <button
                className="text-xs text-blue-500 hover:underline"
                onClick={() => {
                  const updated = [...showScientific];
                  updated[i] = !updated[i];
                  setShowScientific(updated);
                }}
              >
                {showScientific[i] ? "General" : "Scientific"}
              </button>
            </div>

            <p
              className="text-xl font-bold"
              style={{ color: theme?.outputTextColor }}
            >
              {converted
                ? showScientific[i]
                  ? parseScientific(converted)
                  : formatNumber(converted)
                : "-"}
            </p>

            <select
              className="mt-3 w-full border rounded px-2 py-1 bg-white"
              value={selectedUnits[i]}
              onChange={(e) => handleChange(i, e)}
            >
              {units.map((u) => (
                <option key={u.id} value={u.id}>
                  {u.name}
                </option>
              ))}
            </select>
          </div>
        );
      })}

      {[0, 1, 2].map((i) => {
        const selectedItem = realWorldItems.find(
          (item) => item.id === comparisonItems[i]
        );
        const result = compareRealWorld(comparisonItems[i]);

        return (
          <RealWorldBox
            key={i}
            index={i}
            item={selectedItem}
            result={result}
            scientific={showComparisonScientific[i]}
            onToggleScientific={() => {
              const updated = [...showComparisonScientific];
              updated[i] = !updated[i];
              setShowComparisonScientific(updated);
            }}
            items={realWorldItems}
            selectedItemId={comparisonItems[i]}
            onChange={(e) => handleComparisonChange(i, e)}
            theme={theme}
          />
        );
      })}
    </div>
  );
}

export default Converter;
