import React, { useState, useEffect } from "react";
import PocketBase from "pocketbase";
import "./UnitConverter.css";

const pb = new PocketBase("http://127.0.0.1:8090");
pb.autoCancellation(false);

const formatExponent = (val) => {
  if (!val || isNaN(val)) return "...";
  const [base, expRaw] = val.toExponential(2).split("e");
  const exp = expRaw.replace("+", "");
  return (
    <>
      {parseFloat(base).toFixed(2)}×10<sup>{exp}</sup>
    </>
  );
};

const formatNumber = (value, scientific, forceScientific = false) => {
  if (value === null || value === undefined || isNaN(value)) return "...";
  const abs = Math.abs(value);
  if (scientific || forceScientific || (abs !== 0 && (abs >= 1e6 || abs < 1e-3))) {
    return formatExponent(value);
  }
  return parseFloat(value).toLocaleString(undefined, {
    minimumFractionDigits: 2,
    maximumFractionDigits: 6,
  });
};

const RealWorldBox = ({ selected, setSelected, items, baseValue, scientific }) => {
  const sortedItems = [...items].sort((a, b) => a.scientific_value - b.scientific_value);
  const result = selected && baseValue && selected.scientific_value
    ? baseValue / selected.scientific_value
    : null;

  return (
    <div className="bg-white shadow border rounded-lg p-4 flex flex-col gap-3">
      <div className="text-xl font-bold text-blue-600 text-center break-words">
        {formatNumber(result, scientific)}
      </div>
      <select
        value={selected?.id || ""}
        onChange={(e) => {
          const item = items.find((obj) => obj.id === e.target.value);
          if (item) setSelected(item);
        }}
        className="border p-2 rounded w-full text-sm"
      >
        {sortedItems.map((item) => (
          <option key={item.id} value={item.id}>
            {item.name}
          </option>
        ))}
      </select>
      {selected && (
        <div className="text-sm text-gray-700 bg-gray-50 rounded p-2 border">
          <div className="font-semibold text-center">{selected.name}</div>
          <div className="flex justify-between text-xs mt-1">
            <span className="text-blue-600">{formatNumber(selected.scientific_value, true)}</span>
            <span>
              {formatNumber(parseFloat(selected.approx_value), false, true)}{" "}
              {selected.expand?.unit?.symbol || ""}
            </span>
          </div>
          {selected.notes && (
            <div className="text-center text-gray-500 italic mt-2">{selected.notes}</div>
          )}
        </div>
      )}
    </div>
  );
};

export default function TorqueConverter({ selectedCategory }) {
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [selectedFromUnit, setSelectedFromUnit] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [toUnit1, setToUnit1] = useState("");
  const [toUnit2, setToUnit2] = useState("");
  const [toUnit3, setToUnit3] = useState("");
  const [sciConv1, setSciConv1] = useState(false);
  const [sciConv2, setSciConv2] = useState(false);
  const [sciConv3, setSciConv3] = useState(false);
  const [sciReal1, setSciReal1] = useState(false);
  const [sciReal2, setSciReal2] = useState(false);
  const [sciReal3, setSciReal3] = useState(false);
  const [real1, setReal1] = useState(null);
  const [real2, setReal2] = useState(null);
  const [real3, setReal3] = useState(null);

  useEffect(() => {
    const fetchCategory = async () => {
      const categories = await pb.collection("categories").getFullList();
      const match = categories.find((c) => c.name === selectedCategory);
      if (match) setCategoryId(match.id);
    };
    if (selectedCategory) fetchCategory();
  }, [selectedCategory]);

  useEffect(() => {
    if (categoryId) {
      pb.collection("units")
        .getFullList({ filter: `category = "${categoryId}"` })
        .then((units) => {
          const sorted = [...units].sort((a, b) => a.to_base_factor - b.to_base_factor);
          setUnits(sorted);
          setSelectedFromUnit(sorted[0]?.id || "");
          setToUnit1(sorted[0]?.id || "");
          setToUnit2(sorted[1]?.id || "");
          setToUnit3(sorted[2]?.id || "");
        });

      pb.collection("realworld_items")
        .getFullList({ filter: `category = "${categoryId}"`, expand: "unit" })
        .then((items) => {
          setRealWorldItems(items);
          setReal1(items[0]);
          setReal2(items[1]);
          setReal3(items[2]);
        });
    }
  }, [categoryId]);

  const getConverted = (toUnitId, scientific) => {
    if (!inputValue || !selectedFromUnit || !toUnitId) return "";
    const from = units.find((u) => u.id === selectedFromUnit);
    const to = units.find((u) => u.id === toUnitId);
    if (!from || !to) return "";
    const result = (parseFloat(inputValue) * from.to_base_factor) / to.to_base_factor;
    return formatNumber(result, scientific);
  };

  const inputToBase = () => {
    const from = units.find((u) => u.id === selectedFromUnit);
    return from && inputValue ? parseFloat(inputValue) * from.to_base_factor : null;
  };

  if (!selectedCategory) return null;

  return (
    <div className="max-w-6xl mx-auto px-4 py-6 bg-white rounded-lg shadow">
      <div className="flex flex-col md:flex-row gap-6 items-stretch">
        <div className="md:w-1/4 flex flex-col justify-center">
          <div className="space-y-4">
            <h3 className="text-lg font-semibold text-center">From</h3>
            <input
              type="number"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              className="border p-2 rounded w-full"
              placeholder="Enter value"
            />
            <select
              value={selectedFromUnit}
              onChange={(e) => setSelectedFromUnit(e.target.value)}
              className="border p-2 rounded w-full"
            >
              {units.map((u) => (
                <option key={u.id} value={u.id}>
                  {u.name} ({u.symbol})
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="md:w-3/4 space-y-10">
          <div>
            <h2 className="text-2xl font-bold mb-4 text-center">Conversion</h2>
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
              {[toUnit1, toUnit2, toUnit3].map((id, index) => {
                const setId = index === 0 ? setToUnit1 : index === 1 ? setToUnit2 : setToUnit3;
                const sciState = index === 0 ? sciConv1 : index === 1 ? sciConv2 : sciConv3;
                const setSci = index === 0 ? setSciConv1 : index === 1 ? setSciConv2 : setSciConv3;
                return (
                  <div key={index} className="flex flex-col gap-2">
                    <div className="flex justify-center">
                      <button
                        className={`px-3 py-1 rounded-l ${!sciState ? "bg-blue-600 text-white" : "bg-white border"}`}
                        onClick={() => setSci(false)}
                      >
                        General
                      </button>
                      <button
                        className={`px-3 py-1 rounded-r ${sciState ? "bg-blue-600 text-white" : "bg-white border"}`}
                        onClick={() => setSci(true)}
                      >
                        Scientific
                      </button>
                    </div>
                    <div className="border rounded p-4 bg-white shadow-sm">
                      <div className="text-xl font-bold text-blue-600 mb-2 text-center break-words">
                        {getConverted(id, sciState) || "..."}
                      </div>
                      <select
                        value={id}
                        onChange={(e) => setId(e.target.value)}
                        className="border p-2 rounded w-full text-sm text-center"
                      >
                        <option value="">-- Convert To --</option>
                        {units.map((u) => (
                          <option key={u.id} value={u.id}>
                            {u.name} ({u.symbol}) – {getConverted(u.id, sciState)}
                          </option>
                        ))}
                      </select>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>

          <div>
            <h2 className="text-2xl font-bold mb-4 text-center">Comparison</h2>
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
              {[real1, real2, real3].map((realItem, index) => {
                const setReal = index === 0 ? setReal1 : index === 1 ? setReal2 : setReal3;
                const sci = index === 0 ? sciReal1 : index === 1 ? sciReal2 : sciReal3;
                const setSci = index === 0 ? setSciReal1 : index === 1 ? setSciReal2 : setSciReal3;
                return (
                  <div key={index}>
                    <div className="flex justify-center mb-2">
                      <button className={`px-3 py-1 rounded-l ${!sci ? "bg-blue-600 text-white" : "bg-white border"}`} onClick={() => setSci(false)}>General</button>
                      <button className={`px-3 py-1 rounded-r ${sci ? "bg-blue-600 text-white" : "bg-white border"}`} onClick={() => setSci(true)}>Scientific</button>
                    </div>
                    <RealWorldBox selected={realItem} setSelected={setReal} items={realWorldItems} baseValue={inputToBase()} scientific={sci} />
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
