// CategoryPage.jsx
import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import AuxiliaryConverter from "./AuxiliaryConverter";
import Converter from "./Converter";
import FunFacts from "./FunFacts";

function CategoryPage({ categoryName }) {
  const [categoryId, setCategoryId] = useState(null);

  useEffect(() => {
    const fetchCategoryId = async () => {
      const all = await pb.collection("categories").getFullList();
      const match = all.find((c) => c.name === categoryName);
      if (match) setCategoryId(match.id);
    };
    if (categoryName) fetchCategoryId();
  }, [categoryName]);

  if (!categoryId) return <div className="text-center py-10">Loading category...</div>;

  return (
    <div className="space-y-12">
      <AuxiliaryBlock mainCategoryId={categoryId} />
      <div>
        <h2 className="text-2xl font-bold mb-4 text-center text-gray-700">Main Calculation</h2>
        <Converter categoryId={categoryId} />
      </div>
      <FunFacts categoryId={categoryId} />
    </div>
  );
}

export default CategoryPage;
