import React, { useState } from 'react';
import VolumeComparisonFinal from './components/VolumeComparisonFinal';

const categories = [
  "Acceleration", "Angle", "Angular Acceleration", "Angular Velocity",
  "Area", "Capacitance", "Charge", "Current", "Density", "Electric Field",
  "Energy", "Force", "Frequency", "Illuminance", "Inertia", "Length",
  "Mass", "Resistance", "Resistivity", "Specific Heat", "Speed",
  "Surface Tension", "Thermal Conductivity", "Time", "Torque",
  "Viscosity", "Volume", "Voltage"
];

export default function App() {
  const [activeCategory, setActiveCategory] = useState("Volume");

  const renderCategoryComponent = () => {
    switch (activeCategory) {
      case "Volume":
        return <VolumeComparisonFinal />;
      default:
        return (
          <div className="text-center py-12 text-gray-600 text-lg">
            {activeCategory} converter is coming soon...
          </div>
        );
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-100">
      {/* Header */}
      <header className="bg-white shadow py-4 px-6">
        <div className="flex flex-col gap-4 md:flex-row md:items-center md:justify-between">
          <div className="text-2xl font-bold text-blue-600">ðŸ”„ UnitCompare</div>
          <nav className="overflow-x-auto flex gap-2 md:gap-3 pb-2">
            {categories.map((cat) => (
              <button
                key={cat}
                onClick={() => setActiveCategory(cat)}
                className={`whitespace-nowrap px-3 py-1 rounded text-sm font-medium ${
                  activeCategory === cat
                    ? "bg-blue-600 text-white"
                    : "bg-gray-200 text-gray-800"
                }`}
              >
                {cat}
              </button>
            ))}
          </nav>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-grow p-6">{renderCategoryComponent()}</main>

      {/* Footer */}
      <footer className="bg-white border-t text-center text-sm text-gray-500 py-4 mt-6">
        Â© 2025 UnitCompare. All rights reserved.
      </footer>
    </div>
  );
}
