import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import AuxiliaryConverter from "../components/AuxiliaryConverter";
import pb from "../utils/pocketbaseClient";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { useTheme } from "../context/ThemeContext";

const mainCategories = ["length", "temperature", "area", "volume", "mass", "time"];

const toTitleCase = (slug) =>
  slug
    .split("-")
    .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
    .join(" ");

export default function Home() {
  const theme = useTheme();
  const [activeMainCategory, setActiveMainCategory] = useState(mainCategories[0]);
  const [categoryId, setCategoryId] = useState(null);
  const [formData, setFormData] = useState({ name: "", email: "", message: "" });
  const [submitStatus, setSubmitStatus] = useState("");
  const [allCategories, setAllCategories] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCategoryId = async () => {
      try {
        const record = await pb.collection("categories").getFirstListItem(`slug="${activeMainCategory}"`);
        setCategoryId(record.id);
      } catch (err) {
        console.error("Error fetching categoryId:", err);
        setCategoryId(null);
      }
    };
    fetchCategoryId();
  }, [activeMainCategory]);

  useEffect(() => {
    const fetchAllCategories = async () => {
      try {
        const res = await pb.collection("categories").getFullList({ sort: "name" });
        setAllCategories(res);
      } catch (err) {
        console.error("Error fetching all categories:", err);
      }
    };
    fetchAllCategories();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await pb.collection("contact_messages").create(formData);
      setSubmitStatus("Message sent successfully!");
      setFormData({ name: "", email: "", message: "" });
    } catch (err) {
      console.error("Submission failed:", err);
      setSubmitStatus("There was an error. Please try again.");
    }
  };

  return (
    <div
      className="min-h-screen flex flex-col text-gray-800"
      style={{ backgroundColor: theme?.base, fontFamily: theme?.font }}
    >
      <Header />

      <main className="flex-grow px-4 max-w-6xl mx-auto">
        {/* Tab Buttons */}
        <section className="mt-16">
          <div className="flex flex-wrap justify-center gap-4 mb-6">
            {mainCategories.map((slug) => (
              <button
                key={slug}
                onClick={() => setActiveMainCategory(slug)}
                className={`px-4 py-2 rounded font-semibold border transition ${
                  activeMainCategory === slug
                    ? "text-white border-transparent"
                    : "text-gray-700 bg-white border border-gray-300 hover:bg-blue-50"
                }`}
                style={{
                  backgroundColor: activeMainCategory === slug ? theme?.primary : undefined,
                }}
              >
                {toTitleCase(slug)}
              </button>
            ))}
          </div>

          <div className="max-w-3xl mx-auto p-6 rounded shadow" style={{ backgroundColor: theme?.surface }}>
            {categoryId ? (
              <AuxiliaryConverter categoryId={categoryId} />
            ) : (
              <p className="text-center text-gray-500">Loading converter...</p>
            )}
          </div>
        </section>

        {/* All Converters */}
        <section className="mt-16">
          <h2 className="text-3xl font-bold text-center mb-8" style={{ color: theme?.primary }}>
            All Converters
          </h2>
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
            {allCategories.map((cat) => (
              <button
                key={cat.id}
                onClick={() => window.open(`/category/${cat.slug}`, "_blank")}
                className="border border-gray-200 rounded px-4 py-3 text-center text-gray-800 font-medium transition duration-200 hover:bg-blue-50 hover:shadow-md cursor-pointer"
                style={{ backgroundColor: theme?.surface }}
              >
                {toTitleCase(cat.slug)}
              </button>
            ))}
          </div>
        </section>

        {/* Contact Section */}
        <section className="mt-16 mb-8">
          <h2 className="text-3xl font-bold text-center mb-8" style={{ color: theme?.primary }}>
            Contact Us
          </h2>
          <div
            className="shadow rounded-lg overflow-hidden flex flex-col lg:flex-row"
            style={{ backgroundColor: theme?.surface }}
          >
            <div className="w-full lg:w-1/2 bg-gradient-to-br from-blue-500 to-blue-700 p-6 sm:p-8 text-white flex flex-col justify-center items-center text-center">
              <img
                src="/contact-image.svg"
                alt="Contact illustration"
                className="w-full max-w-[200px] sm:max-w-xs mb-4"
              />
              <h3 className="text-2xl font-bold mb-2">Let’s Talk!</h3>
              <p className="text-sm opacity-90 max-w-sm">
                Have questions, suggestions, or just want to say hi? Fill out the form and we’ll get back to you soon.
              </p>
            </div>

            <div className="w-full lg:w-1/2 p-6 sm:p-8">
              <form onSubmit={handleSubmit} className="space-y-4">
                <input
                  type="text"
                  name="name"
                  placeholder="Your Name"
                  className="w-full border border-gray-300 rounded px-4 py-2"
                  style={{ backgroundColor: theme?.base }}
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
                <input
                  type="email"
                  name="email"
                  placeholder="Your Email"
                  className="w-full border border-gray-300 rounded px-4 py-2"
                  style={{ backgroundColor: theme?.base }}
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
                <textarea
                  name="message"
                  placeholder="Your Message"
                  className="w-full border border-gray-300 rounded px-4 py-2"
                  rows="5"
                  style={{ backgroundColor: theme?.base }}
                  value={formData.message}
                  onChange={handleChange}
                  required
                ></textarea>
                <button
                  type="submit"
                  className="text-white px-6 py-2 rounded w-full"
                  style={{ backgroundColor: theme?.primary }}
                >
                  Send Message
                </button>
                {submitStatus && (
                  <p className="text-sm text-center text-gray-600 mt-2">{submitStatus}</p>
                )}
              </form>
            </div>
          </div>
        </section>
      </main>

      {/* Footer Note */}
      <div
        className="text-gray-700 text-sm px-4 py-3 mt-8 max-w-4xl mx-auto rounded"
        style={{ backgroundColor: theme?.surface }}
      >
        <strong>Note:</strong> Some comparison figures are based on approximations, mean values, or estimates.
      </div>

      <Footer />
    </div>
  );
}
