import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import AuxiliaryConverter from "../components/AuxiliaryConverter";
import Converter from "../components/Converter";
import FunFacts from "../components/FunFacts";
import Header from "../components/Header";
import Footer from "../components/Footer";

function CategoryPage({ categoryName }) {
  const [categoryId, setCategoryId] = useState(null);
  const [auxiliaryCategories, setAuxiliaryCategories] = useState([]);
  const [topNote, setTopNote] = useState("");
  const [prefixes, setPrefixes] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const normalizedSlug = categoryName.toLowerCase();
        const mainCategory = await pb.collection("categories").getFirstListItem(
          `slug="${normalizedSlug}"`,
          { expand: "auxiliary" }
        );

        setCategoryId(mainCategory.id);
        setTopNote(mainCategory.top_note || "");

        const auxiliary = Array.isArray(mainCategory.expand?.auxiliary)
          ? mainCategory.expand.auxiliary
          : mainCategory.expand?.auxiliary
          ? [mainCategory.expand.auxiliary]
          : [];

        setAuxiliaryCategories(auxiliary);
        setError(null);
      } catch (err) {
        console.error("Error fetching categories:", err);
        setCategoryId(null);
        setAuxiliaryCategories([]);
        setError("Category not found or error fetching data.");
      }
    };

    const fetchPrefixes = async () => {
      try {
        const prefixData = await pb.collection("prefixes").getFullList({
          sort: "+order",
        });
        setPrefixes(prefixData);
      } catch (err) {
        console.error("Error fetching prefixes:", err);
      }
    };

    if (categoryName) {
      fetchCategories();
      fetchPrefixes();
    }
  }, [categoryName]);

  if (error) {
    return (
      <div className="text-center py-10 text-red-600 font-semibold">
        {error}
      </div>
    );
  }

  if (!categoryId)
    return <div className="text-center py-10">Loading category...</div>;

  const total = auxiliaryCategories.length;

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      <Header />

      <main className="flex-grow px-6 py-10 max-w-7xl mx-auto space-y-16">
        {/* Auxiliary Calculation Section */}
        {total > 0 && (
          <section>
            <h2 className="text-3xl font-bold mb-6 text-center text-gray-700">
              Auxiliary Calculation
            </h2>

            <div className="grid gap-8 sm:grid-cols-2">
              {auxiliaryCategories.map((aux, index) => {
                const isLastOdd =
                  total % 2 === 1 && index === total - 1 && total > 1;
                const isOnlyOne = total === 1;

                return (
                  <div
                    key={aux.id}
                    className={`bg-white p-6 rounded-lg shadow hover:shadow-lg transition ${
                      isLastOdd || isOnlyOne
                        ? "sm:col-span-2 sm:mx-auto sm:w-1/2"
                        : ""
                    }`}
                  >
                    <h3 className="text-xl font-semibold mb-4 text-center text-gray-800">
                      {aux.name}
                    </h3>
                    <AuxiliaryConverter categoryId={aux.id} />
                  </div>
                );
              })}
            </div>
          </section>
        )}

        {/* Main Calculation Section */}
        <section>
          <h2 className="text-3xl font-bold mb-6 text-center text-gray-700">
            Main Calculation
          </h2>
          <div className="bg-white p-6 rounded-lg shadow space-y-4">
            {topNote && (
              <div
                className="text-sm"
                dangerouslySetInnerHTML={{ __html: topNote }}
              />
            )}
            <Converter categoryId={categoryId} />
          </div>
        </section>

        {/* Fun Facts Section */}
        <section>
          <h2 className="text-3xl font-bold mb-6 text-center text-gray-700">
            Fun Facts
          </h2>
          <div className="bg-white px-6 py-6 rounded-lg shadow">
            <FunFacts categoryId={categoryId} />
          </div>
        </section>

        {/* Prefix Table Section */}
        {prefixes.length > 0 && (
          <section>
            <h2 className="text-3xl font-bold mb-6 text-center text-gray-700">
              Prefixes
            </h2>
            <div className="overflow-x-auto bg-white p-6 rounded-lg shadow">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-100 uppercase text-xs font-semibold text-gray-600">
                  <tr>
                    <th className="px-4 py-3 text-left">Name</th>
                    <th className="px-4 py-3 text-left">Symbol</th>
                    <th className="px-4 py-3 text-left">Multiplier</th>
                    <th className="px-4 py-3 text-left">10<sup>n</sup></th>
                    <th className="px-4 py-3 text-left">Description</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200 text-sm">
                  {prefixes.map((prefix, idx) => {
                    const exponential = String(prefix.exponential)
                      .replace(/"/g, "")
                      .replace(/^10\^?/, "");

                    let multiplier = prefix.multiplier;

                    if (
                      typeof multiplier === "string" &&
                      multiplier.toLowerCase().includes("e+")
                    ) {
                      try {
                        const [baseStr, expStr] = multiplier
                          .toLowerCase()
                          .split("e+");
                        const base = parseFloat(baseStr);
                        const exponent = parseInt(expStr);
                        if (!isNaN(base) && !isNaN(exponent)) {
                          if (base === 1) {
                            multiplier = "1" + "0".repeat(exponent);
                          } else {
                            multiplier = (
                              base * 10 ** exponent
                            ).toFixed(0);
                          }
                        }
                      } catch {
                        // fallback to original value
                      }
                    }

                    return (
                      <tr key={idx} className="hover:bg-gray-50">
                        <td className="px-4 py-2">{prefix.name}</td>
                        <td className="px-4 py-2">{prefix.symbol}</td>
                        <td className="px-4 py-2">{multiplier}</td>
                        <td className="px-4 py-2">10<sup>{exponential}</sup></td>
                        <td className="px-4 py-2">{prefix.description}</td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </section>
        )}
      </main>

      <Footer />
    </div>
  );
}

export default CategoryPage;
