import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber } from "../utils/formatNumber";
import { useTheme } from "../context/ThemeContext";
import MoleConverter from "./MoleConverter"; // âœ… Mole-specific component

function parseScientific(val) {
  if (val == null) return null;
  if (typeof val === "number") return val;
  if (typeof val === "object" && "mantissa" in val && "exponent" in val) {
    return val.mantissa * Math.pow(10, val.exponent);
  }
  const num = Number(val);
  return isNaN(num) ? null : num;
}

function Converter({ categoryId }) {
  const theme = useTheme();
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [fromUnit, setFromUnit] = useState("");
  const [categoryInfo, setCategoryInfo] = useState(null);
  const [convertedValues, setConvertedValues] = useState({});
  const [comparisonValues, setComparisonValues] = useState([]);

  const [displayMode, setDisplayMode] = useState("general"); // "general" or "scientific"

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [unitList, realItems, category] = await Promise.all([
          pb.collection("units").getFullList({ filter: `category = "${categoryId}"` }),
          pb.collection("real_world_items").getFullList({ filter: `category = "${categoryId}"` }),
          pb.collection("categories").getOne(categoryId),
        ]);

        setUnits(unitList.sort((a, b) => a.to_base_factor - b.to_base_factor));
        setRealWorldItems(realItems);
        setCategoryInfo(category);

        const topUnit = unitList.find((u) => !u.parent_unit);
        setFromUnit(topUnit?.id || "");
      } catch (err) {
        console.error("Error fetching data:", err);
      }
    };

    if (categoryId) {
      fetchData();
    }
  }, [categoryId]);

  useEffect(() => {
    if (!inputValue || !fromUnit || !units.length) return;

    const from = units.find((u) => u.id === fromUnit);
    if (!from) return;

    const inputInBase = parseScientific(inputValue) * parseScientific(from.to_base_factor);

    const values = {};
    for (const unit of units) {
      const result = inputInBase / parseScientific(unit.to_base_factor);
      values[unit.id] = result;
    }

    setConvertedValues(values);

    const comparisons = realWorldItems.map((item) => {
      const itemValue = parseScientific(item.value);
      const ratio = inputInBase / itemValue;
      return { ...item, ratio };
    });

    setComparisonValues(comparisons);
  }, [inputValue, fromUnit, units]);

  if (categoryInfo?.name?.toLowerCase() === "mole") {
    return (
      <MoleConverter
        categoryId={categoryId}
        units={units}
        inputValue={inputValue}
        setInputValue={setInputValue}
        fromUnit={fromUnit}
        setFromUnit={setFromUnit}
        categoryInfo={categoryInfo}
      />
    );
  }

  return (
    <div
      className="p-4 rounded-2xl"
      style={{
        backgroundColor: theme?.container || "#f3f4f6",
        color: theme?.text || "#1f2937",
      }}
    >
      <h2
        className="text-xl font-semibold mb-4"
        style={{ color: theme?.heading || "#feb73f" }}
      >
        {categoryInfo?.name || "Converter"}
      </h2>

      {/* Input section */}
      <div className="flex flex-col md:flex-row items-center gap-4 mb-6">
        <input
          type="number"
          placeholder="Enter value"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          className="p-2 rounded border"
          style={{
            backgroundColor: theme?.inputBox || "#ffffff",
            color: theme?.text || "#1f2937",
          }}
        />
        <select
          value={fromUnit}
          onChange={(e) => setFromUnit(e.target.value)}
          className="p-2 rounded border"
        >
          {units.map((u) => (
            <option key={u.id} value={u.id}>
              {u.name}
            </option>
          ))}
        </select>
        <div className="flex gap-2">
          <button
            className={`px-3 py-1 rounded ${displayMode === "general" ? "bg-yellow-400" : "bg-gray-300"}`}
            onClick={() => setDisplayMode("general")}
          >
            General
          </button>
          <button
            className={`px-3 py-1 rounded ${displayMode === "scientific" ? "bg-yellow-400" : "bg-gray-300"}`}
            onClick={() => setDisplayMode("scientific")}
          >
            Scientific
          </button>
        </div>
      </div>

      {/* Converted results section */}
      <div className="grid md:grid-cols-3 gap-4 mb-6">
        {units.slice(0, 3).map((unit) => {
          const value = convertedValues[unit.id];
          return (
            <div
              key={unit.id}
              className="p-4 rounded shadow"
              style={{
                backgroundColor: theme?.box || "#ffffff",
              }}
            >
              <div className="text-lg font-bold mb-2">
                {displayMode === "scientific"
                  ? formatNumber(value, "scientific")
                  : formatNumber(value, "general")}
              </div>
              <select className="w-full p-2 rounded border">
                {units.map((u) => (
                  <option key={u.id} value={u.id}>
                    {u.name}
                  </option>
                ))}
              </select>
            </div>
          );
        })}
      </div>

      {/* Real World Comparison section */}
      <div className="grid md:grid-cols-3 gap-4">
        {comparisonValues.slice(0, 3).map((item, idx) => (
          <RealWorldBox
            key={idx}
            item={item}
            ratio={item.ratio}
            displayMode={displayMode}
          />
        ))}
      </div>
    </div>
  );
}

export default Converter;
