import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import { formatNumber, parseScientific } from "../utils/formatNumber";
import { useTheme } from "../context/ThemeContext";

function Converter({ category }) {
  const theme = useTheme();
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [inputValue, setInputValue] = useState(1);
  const [fromUnit, setFromUnit] = useState(null);
  const [toggleStates, setToggleStates] = useState(["general", "general", "general"]);

  useEffect(() => {
    async function fetchUnits() {
      const unitRes = await pb.collection("units").getFullList({
        filter: `category = "${category}"`,
        sort: "+value"
      });
      setUnits(unitRes);
      setFromUnit(unitRes[0]);
    }

    async function fetchRealWorldItems() {
      const itemRes = await pb.collection("real_world_items").getFullList({
        filter: `category = "${category}"`,
        sort: "+value"
      });
      setRealWorldItems(itemRes);
    }

    if (category) {
      fetchUnits();
      fetchRealWorldItems();
    }
  }, [category]);

  function convert(value, from, to) {
    if (!from || !to) return null;
    const baseValue = value * from.to_base_factor;
    return baseValue / to.to_base_factor;
  }

  const convertedValues = units.map((unit) => convert(inputValue, fromUnit, unit));

  function handleToggle(index) {
    setToggleStates((prev) => {
      const newStates = [...prev];
      newStates[index] = newStates[index] === "general" ? "scientific" : "general";
      return newStates;
    });
  }

  return (
    <div className="w-full flex flex-col gap-6">
      <div className="flex flex-col md:flex-row gap-4">
        <input
          type="number"
          value={inputValue}
          onChange={(e) => setInputValue(parseFloat(e.target.value))}
          className="border p-2 rounded w-full md:w-1/3"
        />
        <select
          value={fromUnit?.id}
          onChange={(e) => {
            const selected = units.find((u) => u.id === e.target.value);
            setFromUnit(selected);
          }}
          className="border p-2 rounded w-full md:w-1/3"
        >
          {units.map((unit) => (
            <option key={unit.id} value={unit.id}>{unit.name}</option>
          ))}
        </select>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {[0, 1, 2].map((boxIndex) => (
          <div
            key={boxIndex}
            className="p-4 rounded shadow"
            style={{ backgroundColor: theme?.box || theme?.surface || "#fff" }}
          >
            <div className="flex justify-between items-center mb-2">
              <h3 className="text-lg font-bold">Converted</h3>
              <button
                className="text-sm underline"
                onClick={() => handleToggle(boxIndex)}
              >
                {toggleStates[boxIndex] === "general" ? "Scientific" : "General"}
              </button>
            </div>

            <select className="border rounded w-full mb-2 p-2">
              {units.map((unit, i) => (
                <option key={unit.id} value={unit.id}>{unit.name}</option>
              ))}
            </select>

            <div className="text-xl font-medium">
              {toggleStates[boxIndex] === "scientific"
                ? parseScientific(convertedValues[boxIndex])
                : formatNumber(convertedValues[boxIndex])}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Converter;
