import React from "react";
import parseScientific from "../utils/parseScientific";

function ComparisonBox({
  index,
  inputValue,
  selectedItem,
  setSelectedItem,
  items,
  showScientific,
  setShowScientific,
  theme,
}) {
  const handleSelect = (e) => {
    const selected = items.find((item) => item.id === e.target.value);
    if (selected) {
      setSelectedItem(selected);
    }
  };

  const itemValue = parseScientific(selectedItem?.scientific_value);
  const result = itemValue > 0 ? inputValue / itemValue : null;

  return (
    <div
      className="flex flex-col p-4 rounded-2xl w-full md:w-[32%]"
      style={{ backgroundColor: theme?.boxColor }}
    >
      {/* Result Display */}
      <div className="text-2xl font-semibold mb-2 text-center text-gray-800">
        {result != null ? (showScientific ? result.toExponential(2) : formatNumber(result)) : "–"}
      </div>

      {/* Dropdown to choose real-world object */}
      <select
        value={selectedItem?.id || ""}
        onChange={handleSelect}
        className="p-2 rounded border text-sm"
        style={{ backgroundColor: theme?.inputBoxColor }}
      >
        {items
          .filter((item) => parseScientific(item.scientific_value) > 0)
          .sort(
            (a, b) =>
              parseScientific(a.scientific_value) - parseScientific(b.scientific_value)
          )
          .map((item) => (
            <option key={item.id} value={item.id}>
              {item.name}
            </option>
          ))}
      </select>

      {/* Details */}
      <div className="text-xs mt-2 text-gray-600">
        <div>
          <strong>Scientific:</strong>{" "}
          {selectedItem?.scientific_value ?? "N/A"}
        </div>
        <div>
          <strong>General:</strong>{" "}
          {selectedItem?.general_value ?? "N/A"}
        </div>
        {selectedItem?.notes && (
          <div className="mt-1 italic text-gray-500">{selectedItem.notes}</div>
        )}
      </div>

      {/* Toggle */}
      <div className="mt-2 text-sm text-center">
        <button
          onClick={() => setShowScientific(!showScientific)}
          className="px-3 py-1 border rounded hover:shadow"
          style={{
            backgroundColor: theme?.containerColor,
            color: theme?.textColor,
            borderColor: theme?.borderColor,
          }}
        >
          {showScientific ? "Scientific" : "General"}
        </button>
      </div>
    </div>
  );
}

export default ComparisonBox;
