import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber, parseScientific } from "../utils/formatNumber";
import { useTheme } from "../context/ThemeContext";

function Converter({ category }) {
  const { theme } = useTheme(); // ✅ Now correctly gets the theme
  const [units, setUnits] = useState([]);
  const [items, setItems] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [inputUnit, setInputUnit] = useState("");
  const [results, setResults] = useState([]);
  const [comparisonResults, setComparisonResults] = useState([]);
  const [mode, setMode] = useState("general"); // or "scientific"

  useEffect(() => {
    const fetchData = async () => {
      try {
        const unitsData = await pb.collection("units").getFullList({
          filter: `category.id="${category}"`,
          sort: "+to_base_factor",
        });

        const itemsData = await pb.collection("real_world_items").getFullList({
          filter: `category.id="${category}"`,
          sort: "+value",
        });

        setUnits(unitsData);
        setItems(itemsData);

        if (unitsData.length > 0) {
          setInputUnit(unitsData[0].id);
        }
      } catch (err) {
        console.error("Failed to fetch data:", err);
      }
    };

    fetchData();
  }, [category]);

  useEffect(() => {
    if (!inputValue || !inputUnit || units.length === 0) return;

    const inputUnitData = units.find((u) => u.id === inputUnit);
    if (!inputUnitData) return;

    const baseValue = parseFloat(inputValue) * parseFloat(inputUnitData.to_base_factor);

    const converted = units.map((unit) => {
      const value = baseValue / parseFloat(unit.to_base_factor);
      return {
        id: unit.id,
        name: unit.name,
        value: value,
      };
    });

    const comparisons = items.map((item) => {
      const ratio = baseValue / parseFloat(item.value);
      return {
        id: item.id,
        name: item.name,
        value: ratio,
        generalValue: formatNumber(ratio, "general"),
        scientificValue: formatNumber(ratio, "scientific"),
        note: item.note,
      };
    });

    setResults(converted);
    setComparisonResults(comparisons);
  }, [inputValue, inputUnit, units, items]);

  return (
    <div className="p-4">
      {/* Input section */}
      <div className="mb-4">
        <input
          type="number"
          className="border p-2 rounded mr-2"
          placeholder="Enter value"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
        <select
          className="border p-2 rounded"
          value={inputUnit}
          onChange={(e) => setInputUnit(e.target.value)}
        >
          {units.map((unit) => (
            <option key={unit.id} value={unit.id}>
              {unit.name}
            </option>
          ))}
        </select>
      </div>

      {/* Mode toggle */}
      <div className="mb-4">
        <button
          onClick={() => setMode("general")}
          className={`mr-2 px-4 py-1 rounded ${
            mode === "general" ? "bg-yellow-400 text-white" : "bg-gray-300"
          }`}
        >
          General
        </button>
        <button
          onClick={() => setMode("scientific")}
          className={`px-4 py-1 rounded ${
            mode === "scientific" ? "bg-yellow-400 text-white" : "bg-gray-300"
          }`}
        >
          Scientific
        </button>
      </div>

      {/* Converted Results */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {results.slice(0, 3).map((result) => (
          <div
            key={result.id}
            className="border p-4 rounded"
            style={{
              backgroundColor: theme?.box || "#fff",
              color: theme?.text || "#000",
            }}
          >
            <div className="text-lg font-semibold">
              {mode === "general"
                ? formatNumber(result.value, "general")
                : formatNumber(result.value, "scientific")}
            </div>
            <div className="mt-2">
              <select className="w-full p-2 rounded border">
                <option>{result.name}</option>
              </select>
            </div>
          </div>
        ))}
      </div>

      {/* Real World Comparisons */}
      <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-4">
        {comparisonResults.slice(0, 3).map((item) => (
          <RealWorldBox
            key={item.id}
            name={item.name}
            note={item.note}
            value={
              mode === "general" ? item.generalValue : item.scientificValue
            }
            theme={theme}
          />
        ))}
      </div>
    </div>
  );
}

export default Converter;
