import React, { useEffect, useState } from "react";
import { formatNumber } from "../utils/formatNumber";
import RealWorldBox from "./RealWorldBox";

const ComparisonBox = ({
  index,
  selectedItem,
  inputValue,
  comparisonToggles,
  setComparisonToggles,
  getComparisonValue,
  realWorldItems,
  setSelectedItems,
  theme
}) => {
  const [sortedItems, setSortedItems] = useState([]);

  useEffect(() => {
    if (realWorldItems?.length > 0) {
      const sorted = [...realWorldItems].sort(
        (a, b) => parseFloat(a.scientific_value) - parseFloat(b.scientific_value)
      );
      setSortedItems(sorted);
      // ⛔ Removed internal state setting of selectedItem to avoid override
    }
  }, [realWorldItems]);

  const getDisplayValue = () => {
    if (!selectedItem || !inputValue) return "...";

    return comparisonToggles[index]
      ? selectedItem.expression || formatNumber(getComparisonValue(selectedItem), true)
      : selectedItem.expression || formatNumber(getComparisonValue(selectedItem), false);
  };

  return (
    <div className="p-4 rounded shadow flex flex-col gap-3" style={{ backgroundColor: theme?.box }}>
      <div className="flex justify-center gap-2">
        <button
          className={`px-3 py-1 rounded-l ${
            !comparisonToggles[index] ? "bg-[#FEB73F] text-white" : "bg-white border text-black"
          }`}
          style={{ borderColor: "#ccc" }}
          onClick={() =>
            setComparisonToggles((prev) => prev.map((t, i) => (i === index ? false : t)))
          }
        >
          General
        </button>
        <button
          className={`px-3 py-1 rounded-r ${
            comparisonToggles[index] ? "bg-[#FEB73F] text-white" : "bg-white border text-black"
          }`}
          style={{ borderColor: "#ccc" }}
          onClick={() =>
            setComparisonToggles((prev) => prev.map((t, i) => (i === index ? true : t)))
          }
        >
          Scientific
        </button>
      </div>

      <div className="bg-gray-100 p-3 rounded text-center text-blue-700 font-bold text-base min-h-[48px]">
        {getDisplayValue()} {selectedItem?.expand?.unit?.symbol || ""}
      </div>

      <RealWorldBox
        selected={selectedItem}
        setSelected={(val) =>
          setSelectedItems((prev) => prev.map((item, i) => (i === index ? val : item)))
        }
        items={sortedItems}
        scientificToggle={true}
      />
    </div>
  );
};

export default ComparisonBox;
