// AuxiliaryBlock.jsx
import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import Converter from "./Converter";

function AuxiliaryBlock({ mainCategoryId }) {
  const [auxCategories, setAuxCategories] = useState([]);

  useEffect(() => {
    const fetchAuxiliaries = async () => {
      const category = await pb.collection("categories").getOne(mainCategoryId, {
        expand: "auxiliary",
      });
      setAuxCategories(category.expand?.auxiliary || []);
    };

    if (mainCategoryId) fetchAuxiliaries();
  }, [mainCategoryId]);

  if (!auxCategories.length) return null;

  return (
    <div className="space-y-10">
      <h2 className="text-2xl font-bold mb-4 text-center text-gray-700">
        Auxiliary Calculations
      </h2>

      {auxCategories.map((cat) => (
        <div key={cat.id} className="border rounded-lg shadow p-4">
          <h3 className="text-xl font-semibold text-blue-700 mb-4">
            {cat.name}
          </h3>
          <Converter categoryId={cat.id} compact />
        </div>
      ))}
    </div>
  );
}

export default AuxiliaryBlock;
