import React, { useState, useEffect } from "react";
import PocketBase from "pocketbase";
import "./VolumeConverter.css";

const pb = new PocketBase("http://127.0.0.1:8090");
pb.autoCancellation(false);

// Format exponential as superscript (e.g. 3.2×10⁶)
const formatExponent = (val) => {
  if (!val || isNaN(val)) return "...";
  const [base, expRaw] = val.toExponential(2).split("e");
  const exp = expRaw.replace("+", "");
  return (
    <>
      {parseFloat(base).toFixed(2)}×10<sup>{exp}</sup>
    </>
  );
};

// Format number with commas
const formatThousands = (val) => {
  if (isNaN(val)) return "...";
  return Number(val).toLocaleString("en-US", { maximumFractionDigits: 6 });
};

// Real-world comparison box
const RealWorldBox = ({ selected, setSelected, items, volumeM3 }) => {
  const sortedItems = [...items].sort((a, b) => a.scientific_value - b.scientific_value);

  const result =
    selected && volumeM3 && selected.scientific_value
      ? volumeM3 / selected.scientific_value
      : null;

  return (
    <div className="bg-white shadow-lg border rounded-lg p-4 flex flex-col gap-3">
      {/* Result */}
      <div className="text-xl font-bold text-blue-600 text-center">
        {result ? formatExponent(result) : "..."}
      </div>

      {/* Dropdown */}
      <select
        value={selected?.id || ""}
        onChange={(e) => {
          const item = items.find((obj) => obj.id === e.target.value);
          if (item) setSelected(item);
        }}
        className="border p-2 rounded w-full text-sm"
      >
        {sortedItems.map((item) => (
          <option key={item.id} value={item.id}>
            {item.name}
          </option>
        ))}
      </select>

      {/* Card Body */}
      {selected && (
        <div className="text-sm text-gray-700 bg-gray-50 rounded p-2 border border-gray-200">
          {/* Line 1: Item name */}
          <div className="font-semibold text-center">{selected.name}</div>

          {/* Line 2: Scientific left, Normal right (no label) */}
          <div className="flex justify-between text-xs mt-1">
            <span className="text-blue-600">
              {selected.scientific_value
                ? formatExponent(selected.scientific_value)
                : "..."}
            </span>
            <span className="text-right text-gray-800">
              {formatThousands(selected.approx_value)}{" "}
              {selected.expand?.unit?.symbol || ""}
            </span>
          </div>

          {/* Line 3: Note */}
          {selected.notes && (
            <div className="text-center text-gray-500 italic mt-2">
              {selected.notes}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default function VolumeConverter() {
  const [units, setUnits] = useState([]);
  const [realItems, setRealItems] = useState([]);
  const [categoryId, setCategoryId] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [fromUnit, setFromUnit] = useState("");
  const [to1, setTo1] = useState("");
  const [to2, setTo2] = useState("");
  const [to3, setTo3] = useState("");
  const [real1, setReal1] = useState(null);
  const [real2, setReal2] = useState(null);
  const [real3, setReal3] = useState(null);
  const [scientific, setScientific] = useState(false);

  // Fetch category ID
  useEffect(() => {
    const fetchCategory = async () => {
      const res = await pb.collection("categories").getFullList();
      const volume = res.find((cat) => cat.name === "Volume");
      if (volume) setCategoryId(volume.id);
    };
    fetchCategory();
  }, []);

  // Fetch units
  useEffect(() => {
    if (categoryId) {
      pb.collection("units")
        .getFullList({ filter: `category = "${categoryId}"` })
        .then(setUnits)
        .catch(console.error);
    }
  }, [categoryId]);

  // Fetch real-world items
  useEffect(() => {
    if (categoryId) {
      pb.collection("realworld_items")
        .getFullList({
          filter: `category = "${categoryId}"`,
          expand: "unit",
        })
        .then((items) => {
          setRealItems(items);
          setReal1(items[0]);
          setReal2(items[1]);
          setReal3(items[2]);
        })
        .catch(console.error);
    }
  }, [categoryId]);

  const getM3 = () => {
    const unit = units.find((u) => u.id === fromUnit);
    return unit && inputValue
      ? parseFloat(inputValue) * unit.to_base_factor
      : null;
  };

  const convert = (toId) => {
    if (!inputValue || !fromUnit || !toId) return "";
    const from = units.find((u) => u.id === fromUnit);
    const to = units.find((u) => u.id === toId);
    if (!from || !to) return "";
    const result =
      (parseFloat(inputValue) * from.to_base_factor) / to.to_base_factor;
    return scientific ? formatExponent(result) : formatThousands(result);
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-6 bg-white rounded-lg shadow">
      {/* Toggle */}
      <div className="flex justify-end mb-4">
        <div className="flex items-center border rounded bg-white shadow-sm">
          <button
            className={`px-4 py-1 rounded-l ${
              !scientific ? "bg-blue-600 text-white" : "bg-white"
            }`}
            onClick={() => setScientific(false)}
          >
            Normal
          </button>
          <button
            className={`px-4 py-1 rounded-r ${
              scientific ? "bg-blue-600 text-white" : "bg-white"
            }`}
            onClick={() => setScientific(true)}
          >
            Scientific
          </button>
        </div>
      </div>

      {/* Input */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 items-start">
        {/* Left panel */}
        <div className="space-y-4 mt-8">
          <h3 className="text-lg font-semibold text-center">From</h3>
          <input
            type="number"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            className="border p-2 rounded w-full"
            placeholder="Enter value"
          />
          <select
            value={fromUnit}
            onChange={(e) => setFromUnit(e.target.value)}
            className="border p-2 rounded w-full"
          >
            <option value="">-- Select unit --</option>
            {units.map((u) => (
              <option key={u.id} value={u.id}>
                {u.name} ({u.symbol})
              </option>
            ))}
          </select>
        </div>

        {/* Combined 3-column layout */}
        <div className="md:col-span-3 text-center -mt-6">
          <h2 className="text-2xl font-bold mb-4">Conversion</h2>

          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
            {[0, 1, 2].map((idx) => {
              const unitId = idx === 0 ? to1 : idx === 1 ? to2 : to3;
              const setUnit = idx === 0 ? setTo1 : idx === 1 ? setTo2 : setTo3;
              const realSel = idx === 0 ? real1 : idx === 1 ? real2 : real3;
              const setRealSel = idx === 0 ? setReal1 : idx === 1 ? setReal2 : setReal3;

              return (
                <div key={idx} className="flex flex-col gap-4">
                  {/* Unit conversion */}
                  <div className="border rounded p-4 bg-white shadow-sm">
                    <div className="text-xl font-bold text-blue-600 mb-2">
                      {convert(unitId) || "..."}
                    </div>
                    <select
                      value={unitId}
                      onChange={(e) => setUnit(e.target.value)}
                      className="border p-2 rounded w-full text-sm text-center"
                    >
                      <option value="">-- Convert To --</option>
                      {units.map((u) => (
                        <option key={u.id} value={u.id}>
                          {u.name} ({u.symbol}) – {convert(u.id)}
                        </option>
                      ))}
                    </select>
                  </div>

                  {/* Real world comparison */}
                  <RealWorldBox
                    selected={realSel}
                    setSelected={setRealSel}
                    items={realItems}
                    volumeM3={getM3()}
                  />
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}
