import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber } from "../utils/formatNumber";

function Converter({ categoryId }) {
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [fromUnit, setFromUnit] = useState("");
  const [toUnits, setToUnits] = useState(["", "", ""]);
  const [selectedItems, setSelectedItems] = useState([null, null, null]);
  const [conversionToggles, setConversionToggles] = useState([false, false, false]);
  const [comparisonToggles, setComparisonToggles] = useState([false, false, false]);

  useEffect(() => {
    async function fetchData() {
      try {
        const unitList = await pb.collection("units").getFullList({
          filter: `category = "${categoryId}"`,
        });
        const sortedUnits = unitList.sort((a, b) => a.to_base_factor - b.to_base_factor);
        setUnits(sortedUnits);
        setFromUnit(sortedUnits[0]?.id || "");
        setToUnits([sortedUnits[1]?.id || "", sortedUnits[2]?.id || "", sortedUnits[3]?.id || ""]);

        const realItems = await pb.collection("realworld_items").getFullList({
          filter: `category = "${categoryId}"`,
          expand: "unit",
        });
        const sortedReal = realItems.sort((a, b) => a.scientific_value - b.scientific_value);
        setRealWorldItems(sortedReal);
        setSelectedItems([sortedReal[0] || null, sortedReal[1] || null, sortedReal[2] || null]);
      } catch (err) {
        console.error("Error fetching data:", err);
      }
    }

    if (categoryId) fetchData();
  }, [categoryId]);

  const getConvertedValue = (toUnitId) => {
    const from = units.find((u) => u.id === fromUnit);
    const to = units.find((u) => u.id === toUnitId);
    if (!from || !to || !inputValue) return null;
    const baseValue = parseFloat(inputValue) * from.to_base_factor;
    return baseValue / to.to_base_factor;
  };

  const getComparisonValue = (item) => {
    const from = units.find((u) => u.id === fromUnit);
    const inputNum = Number(inputValue);
    const approx = item?.approx_value;
    if (!from || isNaN(inputNum) || !approx) return null;
    const baseValue = inputNum * from.to_base_factor;
    return baseValue / approx;
  };

  return (
    <div className="flex flex-col gap-8 bg-white rounded shadow p-6">
      <div className="flex flex-col sm:flex-row gap-4 justify-center items-center">
        <input
          type="number"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="Enter value"
          className="border p-2 rounded w-40"
        />
        <div className="flex flex-wrap gap-1 border p-2 rounded w-full md:w-auto max-w-xl max-h-32 overflow-y-auto">
          {units.map((u) => (
            <div
              key={u.id}
              onClick={() => setFromUnit(u.id)}
              className={`cursor-pointer border px-2 py-[2px] rounded text-sm ${
                fromUnit === u.id ? "bg-blue-600 text-white" : "bg-white"
              }`}
            >
              {u.name} ({u.symbol})
            </div>
          ))}
        </div>
      </div>

      {/* Conversion Boxes */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {toUnits.map((toUnitId, index) => (
          <div key={index} className="bg-white p-4 rounded shadow flex flex-col">
            <div className="text-center text-lg font-semibold mb-2">Conversion</div>
            <div className="flex justify-center gap-2 mb-2">
              <button
                className={`px-4 py-1 rounded-l ${!conversionToggles[index] ? "bg-blue-600 text-white" : "bg-white border"}`}
                onClick={() =>
                  setConversionToggles((prev) => {
                    const updated = [...prev];
                    updated[index] = false;
                    return updated;
                  })
                }
              >
                General
              </button>
              <button
                className={`px-4 py-1 rounded-r ${conversionToggles[index] ? "bg-blue-600 text-white" : "bg-white border"}`}
                onClick={() =>
                  setConversionToggles((prev) => {
                    const updated = [...prev];
                    updated[index] = true;
                    return updated;
                  })
                }
              >
                Scientific
              </button>
            </div>
            <div className="bg-gray-100 p-3 rounded text-center text-blue-700 font-bold text-lg min-h-[60px]">
              {formatNumber(getConvertedValue(toUnitId), conversionToggles[index])}{" "}
              {units.find((u) => u.id === toUnitId)?.symbol || ""}
            </div>
            <div className="flex flex-wrap gap-1 mt-2 overflow-y-auto max-h-32">
              {units.map((u) => (
                <div
                  key={u.id}
                  onClick={() =>
                    setToUnits((prev) => {
                      const updated = [...prev];
                      updated[index] = u.id;
                      return updated;
                    })
                  }
                  className={`cursor-pointer border px-2 py-[2px] rounded text-sm ${
                    toUnits[index] === u.id ? "bg-blue-600 text-white" : "bg-white"
                  }`}
                >
                  {u.name} ({u.symbol})
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>

      {/* Comparison Boxes */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {selectedItems.map((item, index) => (
          <div key={index} className="bg-white p-4 rounded shadow flex flex-col">
            <div className="text-center text-lg font-semibold mb-2">Comparison</div>
            <div className="flex justify-center gap-2 mb-2">
              <button
                className={`px-4 py-1 rounded-l ${!comparisonToggles[index] ? "bg-blue-600 text-white" : "bg-white border"}`}
                onClick={() =>
                  setComparisonToggles((prev) => {
                    const updated = [...prev];
                    updated[index] = false;
                    return updated;
                  })
                }
              >
                General
              </button>
              <button
                className={`px-4 py-1 rounded-r ${comparisonToggles[index] ? "bg-blue-600 text-white" : "bg-white border"}`}
                onClick={() =>
                  setComparisonToggles((prev) => {
                    const updated = [...prev];
                    updated[index] = true;
                    return updated;
                  })
                }
              >
                Scientific
              </button>
            </div>
            <div className="bg-gray-100 p-3 rounded mb-2 text-center text-blue-700 font-bold text-lg min-h-[60px]">
              {item && inputValue
                ? `${formatNumber(getComparisonValue(item), comparisonToggles[index])} ${
                    item?.expand?.unit?.symbol || ""
                  }`
                : "..."}
            </div>
            <RealWorldBox
              selected={item}
              setSelected={(selected) =>
                setSelectedItems((prev) => {
                  const updated = [...prev];
                  updated[index] = selected;
                  return updated;
                })
              }
              items={realWorldItems}
              scientificToggle={true}
            />
          </div>
        ))}
      </div>
    </div>
  );
}

export default Converter;
