import React, { useState } from 'react';
import VolumeConverter from "./components/VolumeConverter";
import ForceConverter from "./components/ForceConverter";
import SpecificHeatConverter from "./components/SpecificHeatConverter";
import SurfaceTensionConverter from "./components/SurfaceTensionConverter";
import TorqueConverter from "./components/TorqueConverter";
import AreaConverter from "./components/AreaConverter";
import MassConverter from "./components/MassConverter";
import CurrentConverter from "./components/CurrentConverter";
import ViscosityConverter from "./components/ViscosityConverter";
import MagneticFluxDensityConverter from "./components/MagneticFluxDensityConverter";
import MomentOfInertiaConverter from "./components/MomentOfInertiaConverter";
import TemperatureConverter from "./components/TemperatureConverter";
import YoungsModuleConverter from "./components/YoungsModuleConverter"; // ✅ New import

const categories = [
  "Acceleration", "Angle", "Angular Acceleration", "Angular Velocity",
  "Area", "Capacitance", "Charge", "Current", "Density", "Electric Field",
  "Energy", "Force", "Frequency", "Illuminance", "Length",
  "Magnetic Flux Density", "Mass", "Moment of Inertia", "Resistance", "Resistivity",
  "Specific Heat", "Speed", "Surface Tension", "Thermal Conductivity",
  "Temperature", "Time", "Torque", "Viscosity", "Volume", "Voltage",
  "Young's Modulus" // ✅ Added to button list
];

export default function App() {
  const [activeCategory, setActiveCategory] = useState(null);

  const renderConverter = () => {
    switch (activeCategory) {
      case "Volume":
        return <VolumeConverter selectedCategory="Volume" />;
      case "Force":
        return <ForceConverter selectedCategory="Force" />;
      case "Specific Heat":
        return <SpecificHeatConverter selectedCategory="Specific Heat" />;
      case "Surface Tension":
        return <SurfaceTensionConverter selectedCategory="Surface Tension" />;
      case "Torque":
        return <TorqueConverter selectedCategory="Torque" />;
      case "Area":
        return <AreaConverter selectedCategory="Area" />;
      case "Mass":
        return <MassConverter selectedCategory="Mass" />;
      case "Current":
        return <CurrentConverter selectedCategory="Current" />;
      case "Viscosity":
        return <ViscosityConverter selectedCategory="Viscosity" />;
      case "Magnetic Flux Density":
        return <MagneticFluxDensityConverter selectedCategory="Magnetic Flux Density" />;
      case "Moment of Inertia":
        return <MomentOfInertiaConverter selectedCategory="Moment of Inertia" />;
      case "Temperature":
        return <TemperatureConverter selectedCategory="Temperature" />;
      case "Young's Modulus":
        return <YoungsModuleConverter selectedCategory="Young's Modulus" />; // ✅ Added
      default:
        return (
          <div className="text-center py-12 text-gray-600 text-lg">
            {activeCategory} converter is coming soon...
          </div>
        );
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-100">
      <header className="bg-white shadow py-4 px-6">
        <div className="text-2xl font-bold text-blue-600">🔄 UnitCompare</div>
      </header>

      <main className="flex-grow p-6">
        <h2 className="text-xl font-semibold text-center mb-6">
          Select a Conversion Category
        </h2>
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4 mb-8">
          {categories.map((cat) => (
            <button
              key={cat}
              onClick={() => setActiveCategory(cat)}
              className={`bg-white shadow hover:bg-blue-50 text-gray-800 font-medium border border-gray-200 rounded px-4 py-3 text-center ${activeCategory === cat ? "ring-2 ring-blue-500" : ""}`}
            >
              {cat}
            </button>
          ))}
        </div>
        {activeCategory && renderConverter()}
      </main>

      <footer className="bg-white border-t text-center text-sm text-gray-500 py-4 mt-6">
        © 2025 UnitCompare. All rights reserved.
      </footer>
    </div>
  );
}
