import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import { formatNumber } from "../utils/formatNumber";
import { useTheme } from "../context/ThemeContext";

function MoleConverter({ categoryId }) {
  const [units, setUnits] = useState([]);
  const [inputValue, setInputValue] = useState(1);
  const [fromUnit, setFromUnit] = useState(null);
  const [toUnit, setToUnit] = useState(null);
  const [convertedValue, setConvertedValue] = useState(null);
  const theme = useTheme();

  // Fetch all units in Mole category
  useEffect(() => {
    const fetchUnits = async () => {
      try {
        const records = await pb.collection("units").getFullList({
          filter: `category="${categoryId}"`,
          expand: "parent_unit",
        });

        setUnits(records);
        setFromUnit(records[0]);
        setToUnit(records[1]);
      } catch (err) {
        console.error("Error fetching mole units:", err);
      }
    };

    fetchUnits();
  }, [categoryId]);

  // Conversion Logic
  useEffect(() => {
    if (!fromUnit || !toUnit) return;

    let baseValue = parseFloat(inputValue);

    // Convert to parent (if fromUnit is child)
    if (fromUnit.parent_unit) {
      baseValue = baseValue * fromUnit.factor;
    }

    // Convert from parent to child (if toUnit is child)
    if (toUnit.parent_unit) {
      baseValue = baseValue / toUnit.factor;
    }

    setConvertedValue(baseValue);
  }, [inputValue, fromUnit, toUnit]);

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  return (
    <div className="p-4 bg-white shadow rounded-lg" style={{ backgroundColor: theme?.surface }}>
      <h2 className="text-xl font-bold mb-4" style={{ color: theme?.heading }}>Mole Converter</h2>

      <div className="mb-4">
        <input
          type="number"
          value={inputValue}
          onChange={handleInputChange}
          className="border p-2 rounded w-full"
          placeholder="Enter value"
        />
      </div>

      <div className="flex flex-col md:flex-row gap-4">
        <div className="w-full">
          <label className="block mb-1 font-medium">From:</label>
          <select
            value={fromUnit?.id || ""}
            onChange={(e) => {
              const selected = units.find((u) => u.id === e.target.value);
              setFromUnit(selected);
            }}
            className="border p-2 rounded w-full"
          >
            {units.map((unit) => (
              <option key={unit.id} value={unit.id}>
                {unit.name}
              </option>
            ))}
          </select>
        </div>

        <div className="w-full">
          <label className="block mb-1 font-medium">To:</label>
          <select
            value={toUnit?.id || ""}
            onChange={(e) => {
              const selected = units.find((u) => u.id === e.target.value);
              setToUnit(selected);
            }}
            className="border p-2 rounded w-full"
          >
            {units.map((unit) => (
              <option key={unit.id} value={unit.id}>
                {unit.name}
              </option>
            ))}
          </select>
        </div>
      </div>

      <div className="mt-4">
        <h3 className="text-lg font-semibold" style={{ color: theme?.text }}>
          Result:{" "}
          <span className="font-mono bg-gray-100 px-2 py-1 rounded">
            {formatNumber(convertedValue)} {toUnit?.symbol}
          </span>
        </h3>
      </div>
    </div>
  );
}

export default MoleConverter;
