import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const mainCategories = [
  "length",
  "temperature",
  "area",
  "volume",
  "mass",
  "time"
];

const allCategories = [
  "acceleration",
  "angular-acceleration",
  "angular-velocity",
  "area",
  "capacitance",
  "charge",
  "current",
  "density",
  "electric-field",
  "energy",
  "force",
  "frequency",
  "illuminance",
  "length",
  "magnetic-flux-density",
  "mass",
  "moment-of-inertia",
  "power",
  "pressure",
  "resistance",
  "resistivity",
  "specific-heat",
  "speed",
  "surface-tension",
  "thermal-conductivity",
  "temperature",
  "time",
  "torque",
  "viscosity",
  "volume",
  "voltage",
  "youngs-modulus"
];

// Utility to convert slug to readable title (e.g. "angular-velocity" -> "Angular Velocity")
const toTitleCase = (slug) =>
  slug
    .split("-")
    .map(word => word.charAt(0).toUpperCase() + word.slice(1))
    .join(" ");

export default function Home() {
  const [activeMainCategory, setActiveMainCategory] = useState(mainCategories[0]);
  const navigate = useNavigate();

  return (
    <main className="flex-grow p-6">
      {/* Main Categories as buttons */}
      <section className="max-w-4xl mx-auto mb-12">
        <div className="flex space-x-4 justify-center mb-6">
          {mainCategories.map((slug) => (
            <button
              key={slug}
              onClick={() => setActiveMainCategory(slug)}
              className={`px-4 py-2 rounded font-semibold border ${
                activeMainCategory === slug
                  ? "bg-blue-600 text-white border-blue-600"
                  : "bg-white text-gray-700 border-gray-300 hover:bg-blue-50"
              }`}
            >
              {toTitleCase(slug)}
            </button>
          ))}
        </div>

        <div className="bg-white p-6 rounded shadow">
          {/* Pass slug as categoryName */}
          <AuxiliaryConverter categoryName={activeMainCategory} />
        </div>
      </section>

      {/* All categories grid */}
      <section className="max-w-6xl mx-auto">
        <h2 className="text-xl font-semibold text-center mb-6">All Converters</h2>
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
          {allCategories.map((slug) => (
            <button
              key={slug}
              onClick={() => navigate(`/category/${slug}`)}
              className="bg-white shadow hover:bg-blue-50 text-gray-800 font-medium border border-gray-200 rounded px-4 py-3 text-center"
            >
              {toTitleCase(slug)}
            </button>
          ))}
        </div>
      </section>
    </main>
  );
}
