import React, { useState, useRef, useEffect } from "react";

const realWorldItems = [
  { name: "Hydrogen Atom (electron cloud)", volume: 6.5e-31 },
  { name: "Virus (e.g., Influenza)", volume: 5e-21 },
  { name: "Red Blood Cell", volume: 9e-17 },
  { name: "Human Egg Cell", volume: 1e-12 },
  { name: "Water Droplet", volume: 5e-8 },
  { name: "Teaspoon", volume: 5e-6 },
  { name: "Golf Ball", volume: 4.1e-5 },
  { name: "Tennis Ball", volume: 1.6e-4 },
  { name: "Coffee Cup", volume: 2e-4 },
  { name: "Milk Carton (1L)", volume: 1e-3 },
  { name: "Cooking Pot (5L)", volume: 0.005 },
  { name: "Pressure Cooker (10L)", volume: 0.01 },
  { name: "Standard Oven", volume: 0.07 },
  { name: "Refrigerator Interior", volume: 0.8 },
  { name: "Car Interior (Compact)", volume: 4 },
  { name: "Small Bedroom", volume: 30 },
  { name: "Shipping Container (20ft)", volume: 33 },
  { name: "Swimming Pool (residential)", volume: 80 },
  { name: "Classroom", volume: 240 },
  { name: "Olympic Swimming Pool", volume: 2500 },
  { name: "Empire State Building", volume: 1e6 },
  { name: "Pyramid of Giza", volume: 2.6e6 },
  { name: "Mount Everest", volume: 5.2e13 },
  { name: "Earth", volume: 1.083e21 },
  { name: "Sun", volume: 1.41e27 },
  { name: "UY Scuti", volume: 6.93e36 }
].sort((a, b) => a.volume - b.volume);

const toSuperscript = (n) => {
  const sup = { "-": "⁻", "0": "⁰", "1": "¹", "2": "²", "3": "³", "4": "⁴", "5": "⁵", "6": "⁶", "7": "⁷", "8": "⁸", "9": "⁹" };
  return n.toString().split("").map(c => sup[c] || "").join("");
};

const formatExponent = (value) => {
  if (!value || isNaN(value)) return "-";
  const exp = Math.floor(Math.log10(value));
  return `10${toSuperscript(exp)}`;
};

const formatSci = (val) => {
  if (!val || isNaN(val)) return "-";
  return Number(val).toExponential(2).replace("e+", "×10^").replace("e", "×10^");
};

export default function RealWorldDropdown({ selected, onSelect }) {
  const [open, setOpen] = useState(false);
  const ref = useRef();

  useEffect(() => {
    const close = (e) => {
      if (ref.current && !ref.current.contains(e.target)) setOpen(false);
    };
    document.addEventListener("mousedown", close);
    return () => document.removeEventListener("mousedown", close);
  }, []);

  return (
    <div ref={ref} className="relative w-full">
      <button
        className="border p-2 rounded w-full text-left text-sm bg-white shadow-sm"
        onClick={() => setOpen(!open)}
      >
        <div className="font-semibold">{selected.name}</div>
        <div className="text-xs text-gray-600 mt-1">
          {formatExponent(selected.volume)} m³ &nbsp; {formatSci(selected.volume)} m³
        </div>
      </button>

      {open && (
        <ul className="absolute z-10 mt-1 max-h-72 overflow-y-auto w-full border bg-white rounded shadow text-sm">
          {realWorldItems.map((item) => (
            <li
              key={item.name}
              onClick={() => { onSelect(item); setOpen(false); }}
              className="p-2 border-b hover:bg-blue-50 cursor-pointer"
            >
              <div className="font-medium">{item.name}</div>
              <div className="text-xs text-gray-600">
                {formatExponent(item.volume)} m³ &nbsp; {formatSci(item.volume)} m³
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
