import React, { useState, useEffect } from "react";
import PocketBase from "pocketbase";
import "./VolumeConverter.css";

const pb = new PocketBase("http://127.0.0.1:8090");
pb.autoCancellation(false);

const formatScientific = (val) => {
  if (!val || isNaN(val)) return "...";
  const [base, expRaw] = val.toExponential(2).split("e");
  const exp = expRaw.replace("+", "");
  return (
    <>
      {parseFloat(base).toFixed(2)}×10<sup>{exp}</sup>
    </>
  );
};

const formatNormal = (val) => {
  if (!val || isNaN(val)) return "...";
  return Number(val).toLocaleString("en-US", {
    maximumFractionDigits: 6,
  });
};

const RealWorldBox = ({
  selected,
  setSelected,
  items,
  volumeM3,
  scientific,
}) => {
  const sortedItems = [...items].sort(
    (a, b) => a.scientific_value - b.scientific_value
  );

  const result =
    selected && volumeM3 && selected.scientific_value
      ? volumeM3 / selected.scientific_value
      : null;

  return (
    <div className="bg-white shadow-lg border rounded-lg p-4 flex flex-col gap-3 overflow-hidden">
      <div className="text-xl font-bold text-blue-600 text-center break-words">
        {result
          ? scientific
            ? formatScientific(result)
            : formatNormal(result)
          : "..."}
      </div>

      <select
        value={selected?.id || ""}
        onChange={(e) => {
          const item = items.find((obj) => obj.id === e.target.value);
          if (item) setSelected(item);
        }}
        className="border p-2 rounded w-full text-sm"
      >
        {sortedItems.map((item) => (
          <option key={item.id} value={item.id}>
            {item.name}
          </option>
        ))}
      </select>

      {selected && (
        <div className="text-sm text-gray-700 bg-gray-50 rounded p-2 border border-gray-200">
          <div className="font-semibold text-center">{selected.name}</div>

          <div className="flex justify-between text-xs mt-1">
            <span className="text-left text-blue-600">
              {selected.scientific_value
                ? formatScientific(selected.scientific_value)
                : "..."}
            </span>
            <span className="text-right text-gray-800">
              {selected.approx_value
                ? formatNormal(selected.approx_value) +
                  " " +
                  (selected.expand?.unit?.symbol || "")
                : "..."}
            </span>
          </div>

          {selected.notes && (
            <div className="text-center text-gray-500 italic mt-2">
              {selected.notes}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default function VolumeConverter() {
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [selectedFromUnit, setSelectedFromUnit] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [toUnit1, setToUnit1] = useState("");
  const [toUnit2, setToUnit2] = useState("");
  const [toUnit3, setToUnit3] = useState("");
  const [scientific, setScientific] = useState(false);
  const [real1, setReal1] = useState(null);
  const [real2, setReal2] = useState(null);
  const [real3, setReal3] = useState(null);

  useEffect(() => {
    const fetchCategory = async () => {
      const categories = await pb.collection("categories").getFullList();
      const volume = categories.find((c) => c.name === "Volume");
      if (volume) setCategoryId(volume.id);
    };
    fetchCategory();
  }, []);

  useEffect(() => {
    if (categoryId) {
      pb.collection("units")
        .getFullList({ filter: `category = "${categoryId}"` })
        .then(setUnits)
        .catch(console.error);

      pb.collection("realworld_items")
        .getFullList({ filter: `category = "${categoryId}"`, expand: "unit" })
        .then((items) => {
          setRealWorldItems(items);
          setReal1(items[0]);
          setReal2(items[1]);
          setReal3(items[2]);
        })
        .catch(console.error);
    }
  }, [categoryId]);

  const getConverted = (toUnitId) => {
    if (!inputValue || !selectedFromUnit || !toUnitId) return "...";
    const from = units.find((u) => u.id === selectedFromUnit);
    const to = units.find((u) => u.id === toUnitId);
    if (!from || !to) return "...";

    const result =
      (parseFloat(inputValue) * from.to_base_factor) / to.to_base_factor;

    return scientific ? formatScientific(result) : formatNormal(result);
  };

  const inputToM3 = () => {
    const from = units.find((u) => u.id === selectedFromUnit);
    return from && inputValue
      ? parseFloat(inputValue) * from.to_base_factor
      : null;
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-6 bg-white rounded-lg shadow">
      {/* Toggle */}
      <div className="flex justify-end mb-4">
        <div className="flex items-center border rounded bg-white shadow-sm">
          <button
            className={`px-4 py-1 rounded-l ${
              !scientific ? "bg-blue-600 text-white" : "bg-white"
            }`}
            onClick={() => setScientific(false)}
          >
            Normal
          </button>
          <button
            className={`px-4 py-1 rounded-r ${
              scientific ? "bg-blue-600 text-white" : "bg-white"
            }`}
            onClick={() => setScientific(true)}
          >
            Scientific
          </button>
        </div>
      </div>

      {/* Grid layout */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 items-start">
        {/* Input side */}
        <div className="space-y-4 mt-8 self-center">
          <h3 className="text-lg font-semibold text-center">From</h3>
          <input
            type="number"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            className="border p-2 rounded w-full"
            placeholder="Enter value"
          />
          <select
            value={selectedFromUnit}
            onChange={(e) => setSelectedFromUnit(e.target.value)}
            className="border p-2 rounded w-full"
          >
            <option value="">-- Select unit --</option>
            {units.map((u) => (
              <option key={u.id} value={u.id}>
                {u.name} ({u.symbol})
              </option>
            ))}
          </select>
        </div>

        {/* Right boxes */}
        <div className="md:col-span-3 text-center">
          <h2 className="text-2xl font-bold mb-6">Conversion</h2>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
            {[toUnit1, toUnit2, toUnit3].map((id, index) => {
              const setter =
                index === 0
                  ? setToUnit1
                  : index === 1
                  ? setToUnit2
                  : setToUnit3;
              return (
                <div
                  key={index}
                  className="border rounded p-4 bg-white shadow-sm"
                >
                  <div className="text-xl font-bold text-blue-600 mb-2 break-words">
                    {getConverted(id)}
                  </div>
                  <select
                    value={id}
                    onChange={(e) => setter(e.target.value)}
                    className="border p-2 rounded w-full text-sm text-center"
                  >
                    <option value="">-- Convert To --</option>
                    {units.map((u) => (
                      <option key={u.id} value={u.id}>
                        {u.name} ({u.symbol}) – {getConverted(u.id)}
                      </option>
                    ))}
                  </select>
                </div>
              );
            })}

            {/* Real World Cards */}
            {inputToM3() &&
              [real1, real2, real3].map((real, index) => {
                const setter =
                  index === 0
                    ? setReal1
                    : index === 1
                    ? setReal2
                    : setReal3;
                return (
                  <RealWorldBox
                    key={index}
                    selected={real}
                    setSelected={setter}
                    items={realWorldItems}
                    volumeM3={inputToM3()}
                    scientific={scientific}
                  />
                );
              })}
          </div>
        </div>
      </div>
    </div>
  );
}
