import React, { useEffect, useState } from "react";
import ComparisonBox from "./ComparisonBox";
import { formatNumber } from "../utils/formatNumber";
import { useTheme } from "../context/ThemeContext";

const Converter = ({ units, realWorldItems, categoryId }) => {
  const { theme } = useTheme();
  const [inputValue, setInputValue] = useState("");
  const [selectedItems, setSelectedItems] = useState([null, null, null]);
  const [comparisonToggles, setComparisonToggles] = useState([false, false, false]);

  useEffect(() => {
    if (realWorldItems?.length > 0) {
      const sorted = [...realWorldItems].sort(
        (a, b) => parseFloat(a.scientific_value) - parseFloat(b.scientific_value)
      );
      const defaultSelectable = sorted.filter((item) => item?.scientific_value != null);
      const defaults = [0, 1, 2].map((i) => defaultSelectable[i] || null);
      setSelectedItems(defaults);
    }
  }, [realWorldItems]);

  const getComparisonValue = (item) => {
    if (!inputValue || !item?.scientific_value) return null;
    const val = parseFloat(inputValue) / parseFloat(item.scientific_value);
    return val;
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
      {[0, 1, 2].map((i) => (
        <ComparisonBox
          key={i}
          index={i}
          selectedItem={selectedItems[i]}
          setSelectedItems={setSelectedItems}
          inputValue={inputValue}
          realWorldItems={realWorldItems}
          comparisonToggles={comparisonToggles}
          setComparisonToggles={setComparisonToggles}
          getComparisonValue={getComparisonValue}
          theme={theme}
        />
      ))}
    </div>
  );
};

export default Converter;
