import React, { useState, useEffect } from "react";

const units = [
  "mm³", "cm³", "mL", "in³", "pt (US)", "qt (US)", "L", "gal (US)",
  "ft³", "yd³", "m³", "km³", "mi³"
];

export default function ConverterVertical() {
  const [value, setValue] = useState("");
  const [fromUnit, setFromUnit] = useState("L");
  const [results, setResults] = useState({});

  useEffect(() => {
    if (!value || isNaN(value)) {
      setResults({});
      return;
    }

    const fetchAll = async () => {
      const resObj = {};
      for (const toUnit of units) {
        if (toUnit === fromUnit) continue;
        try {
          const res = await fetch(
            `http://localhost:5000/api/convert/volume?value=${value}&from=${encodeURIComponent(fromUnit)}&to=${encodeURIComponent(toUnit)}`
          );
          const data = await res.json();
          resObj[toUnit] = data.result ?? "Error";
        } catch {
          resObj[toUnit] = "Error";
        }
      }
      setResults(resObj);
    };

    fetchAll();
  }, [value, fromUnit]);

  const columns = [[], [], []];
  const filteredUnits = units.filter((u) => u !== fromUnit);
  filteredUnits.forEach((unit, idx) => {
    columns[idx % 3].push(unit);
  });

  return (
    <div className="max-w-6xl mx-auto bg-white p-6 shadow rounded-lg mt-6">
      <h2 className="text-2xl font-bold mb-6 text-center">Volume Converter</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Left - Input */}
        <div className="space-y-4">
          <input
            type="number"
            value={value}
            onChange={(e) => setValue(e.target.value)}
            placeholder="Enter value"
            className="border p-2 w-full rounded"
          />
          <select
            value={fromUnit}
            onChange={(e) => setFromUnit(e.target.value)}
            className="border p-2 w-full rounded"
          >
            {units.map((unit) => (
              <option key={unit}>{unit}</option>
            ))}
          </select>
        </div>

        {/* Right - Scrollable 3-column result */}
        <div className="border p-4 rounded bg-gray-50 overflow-y-auto max-h-[450px]">
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
            {columns.map((col, i) => (
              <div key={i} className="space-y-4">
                {col.map((unit) => (
                  <div key={unit} className="flex flex-col items-center border rounded p-3 bg-white shadow-sm">
                    <div className="font-bold text-blue-600 text-lg">
                      {results[unit] !== undefined ? results[unit] : "..."}
                    </div>
                    <div className="text-sm text-gray-700 mt-1">{unit}</div>
                  </div>
                ))}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
