import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import { useTheme } from "../context/ThemeContext";
import { formatNumber } from "../utils/formatNumber";

function MoleConverter({ categoryId }) {
  const theme = useTheme();

  const [units, setUnits] = useState([]);
  const [parentUnits, setParentUnits] = useState([]);
  const [selectedParent, setSelectedParent] = useState(null);
  const [filteredUnits, setFilteredUnits] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [selectedUnitId, setSelectedUnitId] = useState(null);
  const [convertedValues, setConvertedValues] = useState({});

  useEffect(() => {
    const fetchUnits = async () => {
      try {
        const unitRes = await pb.collection("units").getFullList({
          filter: `category="${categoryId}"`,
          expand: "parent_unit",
        });

        console.log("All Units from PB:", unitRes);

        const parents = unitRes.filter(u => !u.parent_unit);
        setParentUnits(parents);
        setSelectedParent(parents[0]);
        setUnits(unitRes);
      } catch (err) {
        console.error("Error fetching units:", err);
      }
    };

    fetchUnits();
  }, [categoryId]);

  // Filter child units when parent changes
  useEffect(() => {
    if (!selectedParent || !units.length) return;

    const children = units.filter(
      u => u.parent_unit && u.parent_unit === selectedParent.id
    );

    console.log("Selected Parent:", selectedParent);
    console.log("Child Units of Parent:", children);

    setFilteredUnits(children);
    if (children.length > 0) {
      setSelectedUnitId(children[0].id);
    }
  }, [selectedParent, units]);

  // Conversion logic
  useEffect(() => {
    if (!inputValue || !selectedUnitId || !filteredUnits.length) return;

    const fromUnit = filteredUnits.find(u => u.id === selectedUnitId);
    if (!fromUnit) return;

    const result = {};
    filteredUnits.forEach(toUnit => {
      const baseValue = parseFloat(inputValue) * fromUnit.value;
      const converted = baseValue / toUnit.value;
      result[toUnit.id] = converted;
    });

    console.log("Converted Values:", result);
    setConvertedValues(result);
  }, [inputValue, selectedUnitId, filteredUnits]);

  return (
    <div className="p-4 bg-white rounded-xl shadow" style={{ backgroundColor: theme?.box || "#fff" }}>
      {/* Input Field */}
      <div className="mb-4">
        <input
          type="number"
          value={inputValue}
          onChange={e => setInputValue(e.target.value)}
          placeholder="Enter value"
          className="w-full border p-2 rounded"
        />
      </div>

      {/* Parent Unit Selector */}
      <div className="mb-4">
        <label className="block mb-1">Select Parent Unit</label>
        <select
          className="w-full p-2 border rounded"
          value={selectedParent?.id || ""}
          onChange={e =>
            setSelectedParent(parentUnits.find(p => p.id === e.target.value))
          }
        >
          {parentUnits.map(p => (
            <option key={p.id} value={p.id}>
              {p.name}
            </option>
          ))}
        </select>
      </div>

      {/* Child Unit Selector */}
      <div className="mb-4">
        <label className="block mb-1">Convert From</label>
        <select
          className="w-full p-2 border rounded"
          value={selectedUnitId || ""}
          onChange={e => setSelectedUnitId(e.target.value)}
        >
          {filteredUnits.map(u => (
            <option key={u.id} value={u.id}>
              {u.name}
            </option>
          ))}
        </select>
      </div>

      {/* Output Conversion */}
      <div>
        <h4 className="font-semibold mb-2">Converted Values:</h4>
        <div className="grid grid-cols-1 gap-2">
          {filteredUnits.map(u => (
            <div
              key={u.id}
              className="p-2 border rounded bg-gray-100"
              style={{ backgroundColor: theme?.outputBox || "#f3f4f6" }}
            >
              <div className="font-medium">{u.name}</div>
              <div className="text-sm">
                {formatNumber(convertedValues[u.id]) || "--"}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default MoleConverter;
