import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { useTheme } from "../context/ThemeContext";

function parseScientific(val) {
  if (val == null) return null;
  if (typeof val === "number") return val.toExponential(2);
  if (typeof val === "object" && val.value) return Number(val.value).toExponential(2);
  return Number(val).toExponential(2);
}

function Converter({ categoryId }) {
  const theme = useTheme();

  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);
  const [inputValue, setInputValue] = useState(1);
  const [fromUnit, setFromUnit] = useState(null);
  const [toUnit, setToUnit] = useState(null);
  const [selectedComparisons, setSelectedComparisons] = useState([null, null, null]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const unitData = await pb.collection("units").getFullList({
          filter: `category="${categoryId}"`,
        });

        const sortedUnits = unitData.sort((a, b) => (a.sort_priority || 0) - (b.sort_priority || 0));
        setUnits(sortedUnits);
        setFromUnit(sortedUnits[0]);
        setToUnit(sortedUnits[1] || sortedUnits[0]);

        const items = await pb.collection("realworld_items").getFullList({
          filter: `category="${categoryId}"`,
        });

        const sortedItems = items.sort((a, b) => (a.sort_priority || 0) - (b.sort_priority || 0));
        setRealWorldItems(sortedItems);
        setSelectedComparisons([
          sortedItems[0] || null,
          sortedItems[1] || null,
          sortedItems[2] || null,
        ]);
      } catch (error) {
        console.error("Error loading data:", error);
      }
    };

    fetchData();
  }, [categoryId]);

  const convertValue = () => {
    if (!fromUnit || !toUnit) return null;
    const baseValue = inputValue * fromUnit.to_base_factor;
    return baseValue / toUnit.to_base_factor;
  };

  const getComparisonValue = (item) => {
    if (!fromUnit || !item) return null;
    const baseInput = inputValue * fromUnit.to_base_factor;
    return baseInput / item.base_value;
  };

  return (
    <div>
      {/* Input and Conversion */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
        <div className="flex flex-col gap-2">
          <input
            type="number"
            className="p-2 border rounded"
            value={inputValue}
            onChange={(e) => setInputValue(parseFloat(e.target.value))}
          />
          <select
            className="p-2 border rounded"
            value={fromUnit?.id || ""}
            onChange={(e) =>
              setFromUnit(units.find((u) => u.id === e.target.value))
            }
          >
            {units.map((unit) => (
              <option key={unit.id} value={unit.id}>
                {unit.name}
              </option>
            ))}
          </select>
        </div>
        <div className="flex flex-col gap-2">
          <div className="p-2 border rounded bg-gray-100">
            {convertValue()} {toUnit?.name}
          </div>
          <select
            className="p-2 border rounded"
            value={toUnit?.id || ""}
            onChange={(e) =>
              setToUnit(units.find((u) => u.id === e.target.value))
            }
          >
            {units.map((unit) => (
              <option key={unit.id} value={unit.id}>
                {unit.name}
              </option>
            ))}
          </select>
        </div>
      </div>

      {/* Real-world comparisons */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {selectedComparisons.map((item, index) => (
          <div key={index} className="p-4 border rounded bg-white">
            {item && (
              <>
                <h3 className="font-bold">{item.name}</h3>
                <p>
                  General: {getComparisonValue(item)?.toFixed(2)}
                </p>
                <p>
                  Scientific: {parseScientific(getComparisonValue(item))}
                </p>
              </>
            )}
            <select
              className="mt-2 p-2 border rounded w-full"
              value={item?.id || ""}
              onChange={(e) => {
                const selected = realWorldItems.find((r) => r.id === e.target.value);
                setSelectedComparisons((prev) => {
                  const updated = [...prev];
                  updated[index] = selected;
                  return updated;
                });
              }}
            >
              {realWorldItems.map((rw) => (
                <option key={rw.id} value={rw.id}>
                  {rw.name}
                </option>
              ))}
            </select>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Converter;
