import React from "react";
import { formatNumber } from "../utils/formatNumber";

function RealWorldBox({ selected, setSelected, items = [], scientificToggle = true }) {
  const safeItems = Array.isArray(items) ? items : [];

  // Sort so infinite or expression items come last
  const sortedItems = [...safeItems].sort((a, b) => {
    if (a.expression && !b.expression) return 1;
    if (!a.expression && b.expression) return -1;

    if (typeof a.scientific_value === "number" && typeof b.scientific_value === "number") {
      return a.scientific_value - b.scientific_value;
    }

    return 0;
  });

  return (
    <div
      className="overflow-y-auto max-h-[300px] pr-1 flex flex-col gap-3"
      style={{ overflowX: "hidden" }}
    >
      {sortedItems.map((item) => {
        const isSelected = selected?.id === item.id;

        // Safely handle approx_value if it's not a number
        const approxDisplay =
          typeof item.approx_value === "number"
            ? formatNumber(item.approx_value, false, true)
            : typeof item.approx_value === "string"
            ? item.approx_value
            : "";

        return (
          <div
            key={item.id}
            onClick={() => setSelected?.(item)}
            role="button"
            tabIndex={0}
            onKeyDown={(e) => {
              if (e.key === "Enter" || e.key === " ") setSelected?.(item);
            }}
            className={`cursor-pointer border p-3 rounded shadow-sm ${
              isSelected ? "bg-blue-50 border-blue-400" : "bg-white"
            }`}
          >
            {/* Row 1: Item Name */}
            <div className="font-semibold text-blue-800 text-center">{item.name}</div>

            {/* Row 2: Show expression OR scientific and approx */}
            <div className="text-sm mt-1 flex justify-between text-blue-600 font-mono">
              <span>
                {item.expression ? (
                  <span
                    dangerouslySetInnerHTML={{ __html: item.expression }}
                  />
                ) : (
                  formatNumber(item.scientific_value, true)
                )}
              </span>
              <span>
                {item.expression ? "" : `${approxDisplay} ${item?.expand?.unit?.symbol || ""}`}
              </span>
            </div>

            {/* Row 3: Notes (rich text) */}
            {item.notes && (
              <div
                className="prose max-w-none text-xs text-gray-500 italic mt-2 text-center"
                dangerouslySetInnerHTML={{ __html: item.notes }}
              />
            )}
          </div>
        );
      })}
    </div>
  );
}

export default RealWorldBox;
