import React, { useState, useEffect } from "react";
import PocketBase from "pocketbase";
import "./VolumeConverter.css";

const pb = new PocketBase("http://127.0.0.1:8090");
pb.autoCancellation(false);

const SuperscriptFormat = ({ value }) => {
  const [base, exp] = value.toExponential(2).split("e");
  const cleaned = exp.replace("+", "");
  return (
    <>
      {parseFloat(base).toFixed(2)}×10<sup>{cleaned}</sup>
    </>
  );
};

const RealWorldBox = ({ selected, setSelected, items, volumeM3, useScientific }) => {
  const sortedItems = [...items].sort(
    (a, b) => parseFloat(a.scientific_value) - parseFloat(b.scientific_value)
  );

  const result =
    selected && volumeM3 && selected.scientific_value
      ? volumeM3 / selected.scientific_value
      : null;

  const formatNormal = (val) => {
    const num = parseFloat(val);
    if (isNaN(num)) return "...";
    if (num.toString().includes("e")) {
      const [base, exp] = num.toExponential(2).split("e");
      return (
        <>
          {parseFloat(base).toFixed(2)}×10<sup>{exp.replace("+", "")}</sup>
        </>
      );
    }
    return num.toLocaleString();
  };

  return (
    <div className="bg-white shadow border rounded-lg p-4 flex flex-col gap-3">
      <div className="text-xl font-bold text-blue-600 text-center">
        {result ? (
          useScientific ? <SuperscriptFormat value={result} /> : result.toLocaleString()
        ) : (
          "..."
        )}
      </div>

      <select
        value={selected?.id || ""}
        onChange={(e) => {
          const item = items.find((obj) => obj.id === e.target.value);
          if (item) setSelected(item);
        }}
        className="border p-2 rounded w-full text-sm"
      >
        {sortedItems.map((item) => (
          <option key={item.id} value={item.id}>
            {item.name}
          </option>
        ))}
      </select>

      {selected && (
        <div className="text-sm text-gray-700 bg-gray-50 rounded p-2 border border-gray-200">
          <div className="font-semibold text-center">{selected.name}</div>
          <div className="flex justify-between text-xs mt-1">
            <span className="text-left text-blue-600">
              {/* scientific value without label */}
              {selected.scientific_value ? (
                <SuperscriptFormat value={selected.scientific_value} />
              ) : (
                "..."
              )}
            </span>
            <span className="text-right text-gray-800">
              {formatNormal(selected.approx_value)}{" "}
              {selected.expand?.unit?.symbol || ""}
            </span>
          </div>
          {selected.notes && (
            <div className="text-center text-gray-500 italic mt-2">
              {selected.notes}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default function VolumeConverter() {
  const [units, setUnits] = useState([]);
  const [realItems, setRealItems] = useState([]);
  const [categoryId, setCategoryId] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [selectedFrom, setSelectedFrom] = useState("");
  const [to1, setTo1] = useState("");
  const [to2, setTo2] = useState("");
  const [to3, setTo3] = useState("");
  const [scientific, setScientific] = useState(false);
  const [real1, setReal1] = useState(null);
  const [real2, setReal2] = useState(null);
  const [real3, setReal3] = useState(null);

  useEffect(() => {
    const fetchCategory = async () => {
      const cats = await pb.collection("categories").getFullList();
      const volume = cats.find((c) => c.name === "Volume");
      if (volume) setCategoryId(volume.id);
    };
    fetchCategory();
  }, []);

  useEffect(() => {
    if (categoryId) {
      pb.collection("units")
        .getFullList({ filter: `category = "${categoryId}"` })
        .then(setUnits);
      pb.collection("realworld_items")
        .getFullList({ expand: "unit", filter: `category = "${categoryId}"` })
        .then((items) => {
          setRealItems(items);
          setReal1(items[0]);
          setReal2(items[1]);
          setReal3(items[2]);
        });
    }
  }, [categoryId]);

  const convert = (toId) => {
    if (!inputValue || !selectedFrom || !toId) return "";
    const from = units.find((u) => u.id === selectedFrom);
    const to = units.find((u) => u.id === toId);
    if (!from || !to) return "";
    const result =
      (parseFloat(inputValue) * from.to_base_factor) / to.to_base_factor;
    return scientific ? (
      <SuperscriptFormat value={result} />
    ) : (
      result.toLocaleString()
    );
  };

  const getM3 = () => {
    const from = units.find((u) => u.id === selectedFrom);
    return from && inputValue
      ? parseFloat(inputValue) * from.to_base_factor
      : null;
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-6 bg-white rounded-lg shadow">
      {/* Toggle */}
      <div className="flex justify-end mb-4">
        <div className="flex items-center border rounded bg-white shadow-sm">
          <button
            className={`px-4 py-1 rounded-l ${
              !scientific ? "bg-blue-600 text-white" : "bg-white"
            }`}
            onClick={() => setScientific(false)}
          >
            Normal
          </button>
          <button
            className={`px-4 py-1 rounded-r ${
              scientific ? "bg-blue-600 text-white" : "bg-white"
            }`}
            onClick={() => setScientific(true)}
          >
            Scientific
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 items-start">
        {/* Left Input Panel */}
        <div className="space-y-4 mt-8">
          <h3 className="text-lg font-semibold text-center">From</h3>
          <input
            type="number"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            className="border p-2 rounded w-full"
            placeholder="Enter value"
          />
          <select
            value={selectedFrom}
            onChange={(e) => setSelectedFrom(e.target.value)}
            className="border p-2 rounded w-full"
          >
            <option value="">-- Select unit --</option>
            {units.map((u) => (
              <option key={u.id} value={u.id}>
                {u.name} ({u.symbol})
              </option>
            ))}
          </select>
        </div>

        {/* Right 6 Boxes: 3 Conversion + 3 Comparison */}
        <div className="md:col-span-3 text-center -mt-6">
          <h2 className="text-2xl font-bold mb-4">Conversion</h2>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 mb-10">
            {[to1, to2, to3].map((unitId, idx) => {
              const setTo = idx === 0 ? setTo1 : idx === 1 ? setTo2 : setTo3;
              return (
                <div
                  key={idx}
                  className="border rounded p-4 bg-white shadow-sm flex flex-col items-center"
                >
                  <div className="text-xl font-bold text-blue-600 mb-2">
                    {convert(unitId) || "..."}
                  </div>
                  <select
                    className="border p-2 rounded w-full text-center"
                    value={unitId}
                    onChange={(e) => setTo(e.target.value)}
                  >
                    <option value="">-- Convert To --</option>
                    {units.map((unit) => (
                      <option key={unit.id} value={unit.id}>
                        {unit.name} ({unit.symbol}) – {convert(unit.id)}
                      </option>
                    ))}
                  </select>
                </div>
              );
            })}
          </div>

          {getM3() && (
            <>
              <h2 className="text-2xl font-bold mb-4">Comparison</h2>
              <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
                {[real1, real2, real3].map((r, idx) => {
                  const setR = idx === 0 ? setReal1 : idx === 1 ? setReal2 : setReal3;
                  return (
                    <RealWorldBox
                      key={idx}
                      selected={r}
                      setSelected={setR}
                      items={realItems}
                      volumeM3={getM3()}
                      useScientific={scientific}
                    />
                  );
                })}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}
