import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import AuxiliaryConverter from "../components/AuxiliaryConverter";
import Converter from "../components/Converter";
import FunFacts from "../components/FunFacts";

function CategoryPage({ categoryName }) {
  const [categoryId, setCategoryId] = useState(null);
  const [auxiliaryCategories, setAuxiliaryCategories] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const normalizedSlug = categoryName.toLowerCase();
        const mainCategory = await pb
          .collection("categories")
          .getFirstListItem(`slug="${normalizedSlug}"`, {
            expand: "auxiliary",
          });

        setCategoryId(mainCategory.id);

        const auxiliary = Array.isArray(mainCategory.expand?.auxiliary)
          ? mainCategory.expand.auxiliary
          : mainCategory.expand?.auxiliary
          ? [mainCategory.expand.auxiliary]
          : [];

        setAuxiliaryCategories(auxiliary);
        setError(null);
      } catch (err) {
        console.error("Error fetching categories:", err);
        setCategoryId(null);
        setAuxiliaryCategories([]);
        setError("Category not found or error fetching data.");
      }
    };

    if (categoryName) fetchCategories();
  }, [categoryName]);

  if (error) {
    return (
      <div className="min-h-screen bg-gray-100 text-center py-10 text-red-600 font-semibold">
        {error}
      </div>
    );
  }

  if (!categoryId) {
    return (
      <div className="min-h-screen bg-gray-100 text-center py-10">
        Loading category...
      </div>
    );
  }

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      {/* Header */}
      <header className="bg-white shadow px-6 py-4 w-full">
        <div className="text-center text-2xl font-bold text-blue-700">
          Unit Converter
        </div>
      </header>

      {/* Main content */}
      <main className="flex-grow space-y-12 px-4 sm:px-6 lg:px-8 max-w-5xl mx-auto py-10">
        {/* Auxiliary Section */}
        {auxiliaryCategories.length > 0 && (
          <section>
            <h2 className="text-3xl font-bold mb-6 text-center text-gray-700">
              Auxiliary Calculation
            </h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
              {auxiliaryCategories.map((aux) => (
                <div
                  key={aux.id}
                  className="bg-white p-6 rounded-lg shadow hover:shadow-lg transition"
                >
                  <h3 className="text-xl font-semibold mb-4 text-center text-gray-800">
                    {aux.name}
                  </h3>
                  <AuxiliaryConverter categoryId={aux.id} />
                </div>
              ))}
            </div>
          </section>
        )}

        {/* Main Converter */}
        <section>
          <h2 className="text-3xl font-bold mb-6 text-center text-gray-700">
            Main Calculation
          </h2>
          <div className="bg-white p-6 rounded-lg shadow">
            <Converter categoryId={categoryId} />
          </div>
        </section>

        {/* Fun Facts */}
        <section>
          <h2 className="text-3xl font-bold mb-6 text-center text-gray-700">
            Fun Facts
          </h2>
          <div className="bg-white p-6 rounded-lg shadow">
            <FunFacts categoryId={categoryId} />
          </div>
        </section>
      </main>

      {/* Footer */}
      <footer className="bg-white w-full text-center text-gray-600 py-4 border-t">
        Â© 2025 Unit Converter. All rights reserved.
      </footer>
    </div>
  );
}

export default CategoryPage;
