import React, { useEffect, useState } from "react";
import PocketBase from "pocketbase";
import "./VolumeConverter.css";

const pb = new PocketBase("http://127.0.0.1:8090");
pb.autoCancellation(false);

const VolumeConverter = () => {
  const [units, setUnits] = useState([]);
  const [selectedUnit, setSelectedUnit] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [toUnit1, setToUnit1] = useState("");
  const [toUnit2, setToUnit2] = useState("");
  const [toUnit3, setToUnit3] = useState("");

  useEffect(() => {
    const fetchCategoryId = async () => {
      try {
        const res = await pb.collection("categories").getFullList();
        const volumeCategory = res.find((cat) => cat.name === "Volume");
        if (volumeCategory) setCategoryId(volumeCategory.id);
      } catch (err) {
        console.error("Failed to fetch category ID:", err);
      }
    };
    fetchCategoryId();
  }, []);

  useEffect(() => {
    if (categoryId) {
      pb.collection("units")
        .getFullList({ filter: `category = "${categoryId}"` })
        .then(setUnits)
        .catch(console.error);
    }
  }, [categoryId]);

  const getConvertedValue = (toUnitId) => {
    if (!inputValue || !selectedUnit || !toUnitId) return "";
    const fromUnit = units.find((u) => u.id === selectedUnit);
    const toUnit = units.find((u) => u.id === toUnitId);
    if (!fromUnit || !toUnit) return "";
    const result =
      (parseFloat(inputValue) * fromUnit.to_base_factor) /
      toUnit.to_base_factor;
    return result.toExponential(2).replace("e+", "×10^").replace("e", "×10^");
  };

  return (
    <div className="volume-converter-container p-6 max-w-7xl mx-auto">
      <h2 className="text-xl font-semibold mb-6 text-center">Volume Converter</h2>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 items-start">
        {/* FROM section */}
        <div className="space-y-4">
          <div className="text-lg font-semibold text-center">From</div>
          <input
            type="number"
            placeholder="Enter value"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            className="border p-2 rounded w-full"
          />
          <select
            value={selectedUnit}
            onChange={(e) => setSelectedUnit(e.target.value)}
            className="border p-2 rounded w-full"
          >
            <option value="">-- Select unit --</option>
            {units.map((unit) => (
              <option key={unit.id} value={unit.id}>
                {unit.name} ({unit.symbol})
              </option>
            ))}
          </select>
        </div>

        {/* Convert To Boxes */}
        {[toUnit1, toUnit2, toUnit3].map((toUnit, index) => {
          const setToUnit = [setToUnit1, setToUnit2, setToUnit3][index];
          return (
            <div
              key={index}
              className="border rounded p-4 bg-white shadow flex flex-col justify-start items-center"
            >
              <div className="text-lg font-bold text-blue-600 mb-2">
                {getConvertedValue(toUnit)}
              </div>
              <select
                value={toUnit}
                onChange={(e) => setToUnit(e.target.value)}
                className="border p-2 rounded w-full text-center"
              >
                <option value="">-- Select unit --</option>
                {units.map((unit) => (
                  <option key={unit.id} value={unit.id}>
                    {unit.name} ({unit.symbol}) – {getConvertedValue(unit.id)}
                  </option>
                ))}
              </select>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default VolumeConverter;
