import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import AuxiliaryConverter from "../components/AuxiliaryConverter";
import Converter from "../components/Converter";
import Header from "../components/Header";
import Footer from "../components/Footer";

function CategoryPage({ categoryName }) {
  const [categoryId, setCategoryId] = useState(null);
  const [auxiliaryCategories, setAuxiliaryCategories] = useState([]);
  const [topNote, setTopNote] = useState("");
  const [prefixes, setPrefixes] = useState([]);
  const [funFacts, setFunFacts] = useState("");
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const normalizedSlug = categoryName.toLowerCase();

        const mainCategory = await pb.collection("categories").getFirstListItem(
          `slug="${normalizedSlug}"`,
          { expand: "auxiliary" }
        );

        if (!mainCategory) throw new Error("Category not found");

        setCategoryId(mainCategory.id);
        setTopNote(mainCategory.top_note || "");
        setFunFacts(mainCategory.fun_facts || "");

        // Handle auxiliary: expand or manual
        let auxiliary = [];

        if (Array.isArray(mainCategory.expand?.auxiliary)) {
          auxiliary = mainCategory.expand.auxiliary.map((item) => item.id);
        } else if (Array.isArray(mainCategory.auxiliary)) {
          auxiliary = mainCategory.auxiliary;
        } else if (mainCategory.auxiliary) {
          auxiliary = [mainCategory.auxiliary];
        }

        setAuxiliaryCategories(auxiliary);

        const prefixData = await pb.collection("prefixes").getFullList({
          sort: "+order"
        });
        setPrefixes(prefixData);
      } catch (err) {
        console.error("Error in fetchCategories:", err);
        setError("Could not load this category. Please check the slug or PocketBase data.");
      }
    };

    fetchCategories();
  }, [categoryName]);

  return (
    <>
      <Header />
      <div className="min-h-screen bg-gray-50 text-gray-800 py-8">
        <div className="container mx-auto px-4">
          {error && <div className="text-red-500 text-center mb-6">{error}</div>}

          {/* Section 1: Auxiliary Calculations */}
          <div className="mb-12">
            <h2 className="text-2xl font-semibold mb-4 text-center">Auxiliary Calculations</h2>
            <AuxiliaryConverter categoryIds={auxiliaryCategories} />
          </div>

          {/* Section 2: Main Calculation */}
          <div className="mb-12">
            <h2 className="text-2xl font-semibold mb-4 text-center">Main Calculation</h2>
            <div className="bg-white p-6 rounded shadow-md">
              {topNote && (
                <div className="prose prose-sm max-w-none bg-yellow-50 border-l-4 border-yellow-400 p-4 mb-4 text-yellow-800 rounded">
                  <div dangerouslySetInnerHTML={{ __html: `<strong>Note:</strong> ${topNote}` }} />
                </div>
              )}
              {categoryId && <Converter categoryId={categoryId} />}
            </div>
          </div>

          {/* Section 3: Fun Facts */}
          <div className="mb-12">
            <h2 className="text-2xl font-semibold mb-4 text-center">Fun Facts</h2>
            <div className="prose prose-sm max-w-none bg-white p-4 border rounded shadow">
              <div dangerouslySetInnerHTML={{ __html: funFacts }} />
            </div>
          </div>

          {/* Section 4: Prefix Table */}
          {prefixes.length > 0 && (
            <div className="mb-12 overflow-x-auto">
              <h2 className="text-2xl font-semibold mb-4 text-center">Prefixes</h2>
              <table className="min-w-full bg-white border border-gray-300 text-sm">
                <thead>
                  <tr className="bg-gray-100 text-left">
                    <th className="px-4 py-2 border">Name</th>
                    <th className="px-4 py-2 border">Symbol</th>
                    <th className="px-4 py-2 border">Multiplier</th>
                    <th className="px-4 py-2 border">10<sup>n</sup></th>
                    <th className="px-4 py-2 border">Description</th>
                  </tr>
                </thead>
                <tbody>
                  {prefixes.map((prefix, idx) => (
                    <tr key={idx} className="border-t">
                      <td className="px-4 py-2 border">{prefix.name}</td>
                      <td className="px-4 py-2 border">{prefix.symbol}</td>
                      <td className="px-4 py-2 border">{prefix.multiplier}</td>
                      <td className="px-4 py-2 border">{prefix.exponential}</td>
                      <td className="px-4 py-2 border">{prefix.description}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
      <Footer />
    </>
  );
}

export default CategoryPage;
