import React, { useState, useEffect } from "react";
import PhysicalComparisonFinal from './PhysicalComparisonFinal';

const units = [
  "mm³", "cm³", "mL", "in³", "pt (US)", "qt (US)", "L", "gal (US)",
  "ft³", "yd³", "m³", "km³", "mi³"
];

function ResultColumn({ toUnit, setToUnit, fromValue, fromUnit, results }) {
  return (
    <div className="border rounded p-4 bg-white shadow-sm h-full flex flex-col justify-start items-center">
      <div className="text-xl font-bold text-blue-600 mb-2">
        {toUnit && results[toUnit] !== undefined ? results[toUnit] : "..."}
      </div>
      <select
        className="border p-2 rounded w-full text-center"
        value={toUnit}
        onChange={(e) => setToUnit(e.target.value)}
      >
        {Object.entries(results).map(([unit, value]) => (
          <option key={unit} value={unit}>
            {unit} - {value !== undefined ? value : "..."}
          </option>
        ))}
      </select>
    </div>
  );
}

export default function VolumeComparisonFinal() {
  const [fromValue, setFromValue] = useState("");
  const [fromUnit, setFromUnit] = useState("L");
  const [results, setResults] = useState({});
  const [toUnit1, setToUnit1] = useState("gal (US)");
  const [toUnit2, setToUnit2] = useState("m³");
  const [toUnit3, setToUnit3] = useState("km³");

  useEffect(() => {
    if (!fromValue || isNaN(fromValue)) {
      setResults({});
      return;
    }

    const fetchResults = async () => {
      const resultMap = {};
      for (const unit of units) {
        if (unit === fromUnit) continue;
        try {
          const res = await fetch(
            `http://localhost:5000/api/convert/volume?value=${fromValue}&from=${encodeURIComponent(
              fromUnit
            )}&to=${encodeURIComponent(unit)}`
          );
          const data = await res.json();
          resultMap[unit] = data.result ?? "Error";
        } catch {
          resultMap[unit] = "Error";
        }
      }
      setResults(resultMap);
    };

    fetchResults();
  }, [fromValue, fromUnit]);

  return (
    <div className="max-w-6xl mx-auto p-6 bg-white rounded-lg shadow mt-6">
      {/* Headings */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 items-start mb-2">
        <div></div>
        <div className="md:col-span-3 text-center text-lg font-semibold">
          Convert To
        </div>
      </div>

      {/* Input and Result Columns */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 items-start">
        {/* Input Column */}
        <div className="space-y-4">
          <div className="text-lg font-semibold text-center mb-2">From</div>
          <input
            type="number"
            placeholder="Enter value"
            value={fromValue}
            onChange={(e) => setFromValue(e.target.value)}
            className="border p-2 rounded w-full"
          />
          <select
            value={fromUnit}
            onChange={(e) => setFromUnit(e.target.value)}
            className="border p-2 rounded w-full"
          >
            {units.map((unit) => (
              <option key={unit}>{unit}</option>
            ))}
          </select>
        </div>

        {/* 3 Result Columns */}
        <ResultColumn
          toUnit={toUnit1}
          setToUnit={setToUnit1}
          fromValue={fromValue}
          fromUnit={fromUnit}
          results={results}
        />
        <ResultColumn
          toUnit={toUnit2}
          setToUnit={setToUnit2}
          fromValue={fromValue}
          fromUnit={fromUnit}
          results={results}
        />
        <ResultColumn
          toUnit={toUnit3}
          setToUnit={setToUnit3}
          fromValue={fromValue}
          fromUnit={fromUnit}
          results={results}
        />
      </div>

      {/* Physical Comparison Section */}
      <PhysicalComparisonFinal volumeM3={results["m³"]} />

    </div>
  );
}
