import React, { useState, useEffect } from "react";
import PocketBase from "pocketbase";

const pb = new PocketBase("http://127.0.0.1:8090");
pb.autoCancellation(false);

const Dropdown = ({ items, selected, onSelect }) => (
  <select
    value={selected?.id || ""}
    onChange={(e) => {
      const item = items.find((obj) => obj.id === e.target.value);
      if (item) onSelect(item);
    }}
    className="border p-2 rounded w-full text-sm"
  >
    {items.map((item) => (
      <option key={item.id} value={item.id}>
        {item.name} ▪ {item.value_si} m³ ▪ {item.normal_value}
      </option>
    ))}
  </select>
);

const ItemBox = ({ selected, setSelected, items, inputVolume }) => {
  const result =
    selected && inputVolume && selected.value_si
      ? inputVolume / selected.value_si
      : null;

  const format = (val) => {
    if (!val || isNaN(val)) return "...";
    return Number(val).toExponential(2)
      .replace("e+", "×10^")
      .replace("e", "×10^")
      .replace(/\^([-+]?)(\d+)/, (_, sign, exp) =>
        `^${sign === "+" ? "" : sign}${exp}`
      );
  };

  return (
    <div className="border rounded p-4 bg-white shadow-sm flex flex-col items-center justify-between gap-2">
      <div className="text-xl font-bold text-blue-600 mb-1">
        {format(result)}
      </div>
      <Dropdown items={items} selected={selected} onSelect={setSelected} />
      <div className="text-xs text-gray-500 mt-2 text-center">
        {selected?.note || ""}
      </div>
    </div>
  );
};

export default function RealWorldVolumeCompare({ inputVolume }) {
  const [items, setItems] = useState([]);
  const [selected1, setSelected1] = useState(null);
  const [selected2, setSelected2] = useState(null);
  const [selected3, setSelected3] = useState(null);

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const catRes = await pb.collection("categories").getFullList();
        const volumeCat = catRes.find((cat) => cat.name === "Volume");
        if (!volumeCat) return;

        const records = await pb
          .collection("realworld")
          .getFullList({ filter: `category = "${volumeCat.id}"` });

        setItems(records);
        setSelected1(records[0]);
        setSelected2(records[1]);
        setSelected3(records[2]);
      } catch (err) {
        console.error("Failed to fetch real-world items", err);
      }
    };

    fetchItems();
  }, []);

  return (
    <div className="mt-10">
      <h3 className="text-lg font-semibold text-center mb-4">Compare To</h3>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <ItemBox
          selected={selected1}
          setSelected={setSelected1}
          items={items}
          inputVolume={inputVolume}
        />
        <ItemBox
          selected={selected2}
          setSelected={setSelected2}
          items={items}
          inputVolume={inputVolume}
        />
        <ItemBox
          selected={selected3}
          setSelected={setSelected3}
          items={items}
          inputVolume={inputVolume}
        />
      </div>
    </div>
  );
}
