import React, { useEffect, useState } from "react";
import pb from "../utils/pocketbaseClient";
import RealWorldBox from "./RealWorldBox";
import { formatNumber } from "../utils/formatNumber";

/* --- simple inline Header & Footer (white) --- */
const MiniHeader = () => (
  <header className="w-full bg-white shadow-sm px-4 py-3 text-blue-700 font-semibold text-lg">
    AuxiliaryÂ Converter
  </header>
);
const MiniFooter = () => (
  <footer className="w-full bg-white border-t text-gray-500 text-center text-sm py-3 mt-6">
    EndÂ ofÂ Converter
  </footer>
);

export default function AuxiliaryConverter({ categoryName }) {
  const [category, setCategory] = useState(null);
  const [units, setUnits] = useState([]);
  const [realWorldItems, setRealWorldItems] = useState([]);

  /* UI states */
  const [inputValue, setInputValue] = useState("");
  const [fromUnit, setFromUnit] = useState("");
  const [toUnit, setToUnit] = useState("");
  const [selectedItem, setSelectedItem] = useState(null);
  const [conversionToggle, setConversionToggle] = useState(false);
  const [comparisonToggle, setComparisonToggle] = useState(false);
  const [error, setError] = useState(null);

  /* 1) get category by slug (lowercase, hyphens allowed) */
  useEffect(() => {
    async function fetchCategory() {
      try {
        const recs = await pb.collection("categories").getFullList({
          filter: `slug="${categoryName}"`,
          limit: 1,
        });
        if (!recs.length) {
          setError("Category not found"); return;
        }
        setCategory(recs[0]);
        setError(null);
      } catch (err) {
        console.error("Category fetch error:", err);
        setError("Error fetching category");
      }
    }
    if (categoryName) fetchCategory();
  }, [categoryName]);

  /* 2) once category present, load units + realâ€‘world items */
  useEffect(() => {
    async function fetchData() {
      if (!category) return;
      try {
        const unitList = await pb.collection("units").getFullList({
          filter: `category = "${category.id}"`,
        });
        const sortedUnits = unitList.sort((a, b) => a.to_base_factor - b.to_base_factor);
        setUnits(sortedUnits);
        setFromUnit(sortedUnits[0]?.id || "");
        setToUnit(sortedUnits[0]?.id || "");

        const rwItems = await pb.collection("realworld_items").getFullList({
          filter: `category = "${category.id}"`,
          expand: "unit",
        });
        const sortedRw = rwItems.sort((a, b) => a.scientific_value - b.scientific_value);
        setRealWorldItems(sortedRw);
        setSelectedItem(sortedRw[0] || null);
      } catch (err) {
        console.error("Unit/RW fetch error:", err);
        setError("Error loading data");
      }
    }
    fetchData();
  }, [category]);

  /* helpers */
  const getConvertedValue = (overrideToUnit = null) => {
    const from = units.find((u) => u.id === fromUnit);
    const to = units.find((u) => u.id === (overrideToUnit || toUnit));
    if (!from || !to || !inputValue) return null;
    const base = parseFloat(inputValue) * from.to_base_factor;
    return base / to.to_base_factor;
  };
  const getComparisonValue = () => {
    const from = units.find((u) => u.id === fromUnit);
    const approx = selectedItem?.approx_value;
    if (!from || !approx || !inputValue) return null;
    const base = parseFloat(inputValue) * from.to_base_factor;
    return base / approx;
  };

  return (
    <>
      <MiniHeader />

      <div className="bg-gray-100 p-6">
        {error && <p className="text-red-600">{error}</p>}
        {!category && !error && <p>Loading category...</p>}
        {category && (
          <div className="bg-white p-6 rounded shadow max-w-2xl mx-auto">
            <h2 className="text-xl font-bold mb-4 text-center">{category.name}</h2>

            {/* --- input + unit pickers --- */}
            <div className="flex flex-col sm:flex-row gap-4 justify-center items-center mb-6">
              <input
                type="number"
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                placeholder="Enter value"
                className="border p-2 rounded w-40"
              />
              <select
                value={fromUnit}
                onChange={(e) => setFromUnit(e.target.value)}
                className="border p-2 rounded w-48"
              >
                {units.map((u) => (
                  <option key={u.id} value={u.id}>
                    {u.name} ({u.symbol})
                  </option>
                ))}
              </select>
            </div>

            {/* --- Conversion + Comparison --- */}
            <div className="flex flex-col lg:flex-row gap-6">
              {/* Conversion */}
              <div className="flex-1 bg-gray-50 p-4 rounded shadow">
                <div className="text-center font-semibold mb-2">Conversion</div>
                <div className="bg-gray-100 text-blue-700 font-bold text-center p-2 rounded mb-2">
                  {formatNumber(getConvertedValue(), conversionToggle)}{" "}
                  {units.find((u) => u.id === toUnit)?.symbol || ""}
                </div>
                <div className="overflow-y-auto max-h-48 border rounded">
                  {units.map((u) => (
                    <div
                      key={u.id}
                      onClick={() => setToUnit(u.id)}
                      className={`px-2 py-1 text-sm cursor-pointer ${
                        u.id === toUnit
                          ? "bg-blue-50 font-semibold"
                          : "hover:bg-gray-100"
                      }`}
                    >
                      {u.name} ({u.symbol}) â€” {formatNumber(getConvertedValue(u.id))}
                    </div>
                  ))}
                </div>
              </div>

              {/* Comparison */}
              <div className="flex-1 bg-gray-50 p-4 rounded shadow">
                <div className="text-center font-semibold mb-2">Comparison</div>
                <div className="bg-gray-100 text-blue-700 font-bold text-center p-2 rounded mb-2">
                  {formatNumber(getComparisonValue())}{" "}
                  {selectedItem?.expand?.unit?.symbol || ""}
                </div>
                <RealWorldBox
                  selected={selectedItem}
                  setSelected={setSelectedItem}
                  items={realWorldItems}
                />
              </div>
            </div>
          </div>
        )}
      </div>

      <MiniFooter />
    </>
  );
}
