import React, { useEffect, useState } from "react";
import PocketBase from "pocketbase";
import "./VolumeConverter.css";

const pb = new PocketBase("http://127.0.0.1:8090");
pb.autoCancellation(false);

const VolumeConverter = () => {
  const [units, setUnits] = useState([]);
  const [categoryId, setCategoryId] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [selectedFromUnit, setSelectedFromUnit] = useState("");
  const [toUnit1, setToUnit1] = useState("");
  const [toUnit2, setToUnit2] = useState("");
  const [toUnit3, setToUnit3] = useState("");
  const [useScientific, setUseScientific] = useState(false);

  useEffect(() => {
    const fetchCategoryId = async () => {
      try {
        const res = await pb.collection("categories").getFullList();
        const volumeCategory = res.find((cat) => cat.name === "Volume");
        if (volumeCategory) setCategoryId(volumeCategory.id);
      } catch (err) {
        console.error("Failed to fetch category ID:", err);
      }
    };
    fetchCategoryId();
  }, []);

  useEffect(() => {
    if (categoryId) {
      pb.collection("units")
        .getFullList({ filter: `category = "${categoryId}"` })
        .then(setUnits)
        .catch(console.error);
    }
  }, [categoryId]);

  const getConvertedValue = (toUnitId) => {
    if (!inputValue || !selectedFromUnit || !toUnitId) return "";
    const fromUnit = units.find((u) => u.id === selectedFromUnit);
    const toUnit = units.find((u) => u.id === toUnitId);
    if (!fromUnit || !toUnit) return "";

    const rawValue =
      (parseFloat(inputValue) * fromUnit.to_base_factor) / toUnit.to_base_factor;

    return useScientific
      ? rawValue.toExponential(2).replace("e", "×10^")
      : rawValue.toFixed(6);
  };

  return (
    <div className="converter-container">
      {/* Title + Toggle Row */}
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-bold text-center flex-1">Volume Converter</h2>
        <div className="toggle-container">
          <span className="mr-2 font-medium">Normal / Scientific:</span>
          <label className="toggle-switch">
            <input
              type="checkbox"
              checked={useScientific}
              onChange={() => setUseScientific(!useScientific)}
            />
            <span className="slider"></span>
          </label>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 items-start">
        {/* From Column */}
        <div className="space-y-4">
          <label className="block font-semibold">Enter value:</label>
          <input
            type="number"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            className="border p-2 rounded w-full"
          />
          <label className="block font-semibold">From unit:</label>
          <select
            className="border p-2 rounded w-full"
            value={selectedFromUnit}
            onChange={(e) => setSelectedFromUnit(e.target.value)}
          >
            <option value="">-- Select unit --</option>
            {units.map((unit) => (
              <option key={unit.id} value={unit.id}>
                {unit.name} ({unit.symbol})
              </option>
            ))}
          </select>
        </div>

        {/* 3 Convert To Columns */}
        {[toUnit1, toUnit2, toUnit3].map((unitId, index) => {
          const setToUnit =
            index === 0 ? setToUnit1 : index === 1 ? setToUnit2 : setToUnit3;
          return (
            <div
              key={index}
              className="border rounded p-4 bg-white shadow-sm flex flex-col items-center"
            >
              <div className="text-xl font-bold text-blue-600 mb-2">
                {getConvertedValue(unitId) || "..."}
              </div>
              <select
                className="border p-2 rounded w-full text-center"
                value={unitId}
                onChange={(e) => setToUnit(e.target.value)}
              >
                <option value="">-- Convert To --</option>
                {units.map((unit) => (
                  <option key={unit.id} value={unit.id}>
                    {unit.name} ({unit.symbol}) - {getConvertedValue(unit.id) || "..."}
                  </option>
                ))}
              </select>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default VolumeConverter;
