import React, { useState } from 'react';

const unitMap = {
  volume: [
    "mm³", "cm³", "mL", "in³", "pt (US)", "qt (US)", "L", "gal (US)", "ft³", "yd³", "m³", "km³", "mi³"
  ],
  power: [
    "fW", "pW", "nW", "µW", "mW", "W", "kW", "MW", "GW", "TW", "PW", "EW", "hp", "cal/s", "kcal/h", "kcal/min", "Btu/h", "lbf·ft/s"
  ]
};

export default function Converter() {
  const [category, setCategory] = useState("volume");
  const [value, setValue] = useState("");
  const [fromUnit, setFromUnit] = useState(unitMap.volume[0]);
  const [toUnit, setToUnit] = useState(unitMap.volume[1]);
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");

  const handleCategoryChange = (newCategory) => {
    setCategory(newCategory);
    setFromUnit(unitMap[newCategory][0]);
    setToUnit(unitMap[newCategory][1]);
    setValue("");
    setResult(null);
    setError("");
  };

  const convert = async () => {
    if (!value || isNaN(value)) {
      setError("Please enter a valid number.");
      setResult(null);
      return;
    }

    setError("");
    try {
      const response = await fetch(`http://localhost:5000/api/convert/${category}?value=${value}&from=${encodeURIComponent(fromUnit)}&to=${encodeURIComponent(toUnit)}`);
      const data = await response.json();

      if (data.error) {
        setError(data.error);
        setResult(null);
      } else {
        setResult(data.result);
      }
    } catch (err) {
      console.error(err);
      setError("Backend error or server not reachable.");
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white shadow rounded-lg mt-6">
      <h2 className="text-2xl font-semibold text-center mb-6">Unit Converter</h2>

      <div className="flex justify-center mb-4 gap-3 flex-wrap">
        {Object.keys(unitMap).map((cat) => (
          <button
            key={cat}
            className={`px-4 py-2 rounded-full border text-sm font-medium ${
              category === cat ? "bg-blue-600 text-white" : "bg-gray-100 text-gray-800"
            }`}
            onClick={() => handleCategoryChange(cat)}
          >
            {cat.charAt(0).toUpperCase() + cat.slice(1)}
          </button>
        ))}
      </div>

      <div className="grid sm:grid-cols-3 gap-4 mb-4">
        <input
          type="number"
          value={value}
          onChange={(e) => setValue(e.target.value)}
          placeholder="Enter value"
          className="p-2 border rounded col-span-3 sm:col-span-1"
        />
        <select className="p-2 border rounded" value={fromUnit} onChange={(e) => setFromUnit(e.target.value)}>
          {unitMap[category].map((u) => <option key={u}>{u}</option>)}
        </select>
        <select className="p-2 border rounded" value={toUnit} onChange={(e) => setToUnit(e.target.value)}>
          {unitMap[category].map((u) => <option key={u}>{u}</option>)}
        </select>
      </div>

      <div className="text-center mt-4">
        <button
          onClick={convert}
          className="px-6 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition"
        >
          Convert
        </button>
      </div>

      {error && <p className="text-red-600 mt-4 text-center">{error}</p>}

      {result !== null && (
        <p className="text-xl font-semibold text-center mt-6">
          Result: {value} {fromUnit} = {result} {toUnit}
        </p>
      )}
    </div>
  );
}
